```typescript
// Input sanitization to prevent XSS attacks
// src/lib/validators.ts
function sanitizeString(input: string): string {
  return input
    .replace(/[<>"'&]/g, (match) => {
      const escapeMap: Record<string, string> = {
        '<': '&lt;',
        '>': '&gt;',
        '"': '&quot;',
        "'": '&#x27;',
        '&': '&amp;'
      };
      return escapeMap[match];
    })
    .trim()
    .slice(0, 255); // Limit length to prevent buffer overflow
}

// Custom string validation with sanitization
const sanitizedString = (minLength?: number) => {
  if (minLength !== undefined) {
    return z.string()
      .min(minLength, `Minimum length is ${minLength} characters`)
      .transform(sanitizeString);
  }
  return z.string().transform(sanitizeString);
};

// Phone number validation and formatting
export const phoneRegex = /^\d{3}-\d{3}-\d{4}$/;
const phoneSchema = z.string()
  .optional()
  .refine((val) => !val || phoneRegex.test(val), {
    message: 'Phone number must be in format 123-456-7890'
  })
  .transform((val) => val ? sanitizeString(val) : val);
```

**2. RSVP FORM SCHEMA**
```typescript
// Complete RSVP form validation schema
// src/lib/validators.ts
export const coreRsvpFormSchema = z.object({
  firstName: sanitizedString(1).refine(
    (val) => val.length >= 1 && val.length <= 50,
    { message: 'First name must be between 1 and 50 characters' }
  ),
  lastName: sanitizedString(1).refine(
    (val) => val.length >= 1 && val.length <= 50,
    { message: 'Last name must be between 1 and 50 characters' }
  ),
  email: z.string()
    .email('Valid email is required')
    .max(254, 'Email must be less than 254 characters')
    .transform(sanitizeString),
  phone: phoneSchema,
  organization: sanitizedString().optional().refine(
    (val) => !val || val.length <= 100,
    { message: 'Organization name must be less than 100 characters' }
  ),
  attendanceStatus: z.enum(['YES', 'NO', 'MAYBE'], {
    errorMap: () => ({ message: 'Please select an attendance status' })
  }),
  attendeeCount: z.number()
    .int('Attendee count must be a whole number')
    .min(1, 'At least 1 attendee is required')
    .max(10, 'Maximum 10 attendees allowed'),
  dietaryPreference: z.enum(['NONE', 'VEGETARIAN', 'VEGAN', 'GLUTEN_FREE', 'OTHER'], {
    errorMap: () => ({ message: 'Please select a dietary preference' })
  }),
  dietaryOther: sanitizedString().optional().refine(
    (val) => !val || val.length <= 200,
    { message: 'Dietary other must be less than 200 characters' }
  ),
  accessibilityNeeds: sanitizedString().optional().refine(
    (val) => !val || val.length <= 500,
    { message: 'Accessibility needs must be less than 500 characters' }
  ),
  referralSource: z.enum(['WORD_OF_MOUTH', 'SOCIAL_MEDIA', 'WEBSITE', 'EMAIL', 'OTHER'], {
    errorMap: () => ({ message: 'Please select a referral source' })
  }),
  referralOther: sanitizedString().optional().refine(
    (val) => !val || val.length <= 200,
    { message: 'Referral other must be less than 200 characters' }
  ),
  wantsResources: z.boolean().default(false),
  wantsAudit: z.boolean().default(false),
  learningGoal: sanitizedString().optional().refine(
    (val) => !val || val.length <= 1000,
    { message: 'Learning goal must be less than 1000 characters' }
  )
});

// Conditional validation for RSVP form
export const rsvpSchema = coreRsvpFormSchema
  .refine((data) => {
    // If dietary preference is OTHER, dietaryOther is required
    if (data.dietaryPreference === 'OTHER' && !data.dietaryOther) {
      return false;
    }
    return true;
  }, {
    message: 'Please specify dietary requirements when selecting OTHER',
    path: ['dietaryOther']
  })
  .refine((data) => {
    // If referral source is OTHER, referralOther is required
    if (data.referralSource === 'OTHER' && !data.referralOther) {
      return false;
    }
    return true;
  }, {
    message: 'Please specify referral source when selecting OTHER',
    path: ['referralOther']
  })
  .refine((data) => {
    // If attendance status is YES, attendeeCount must be at least 1
    if (data.attendanceStatus === 'YES' && data.attendeeCount < 1) {
      return false;
    }
    return true;
  }, {
    message: 'Attendee count must be at least 1 when attending',
    path: ['attendeeCount']
  });
