// SendGrid webhook endpoint
// src/app/api/webhooks/sendgrid/route.ts
import { NextRequest, NextResponse } from 'next/server';
import { handleSendGridWebhook } from '@/lib/sendgrid-email';

export async function POST(req: NextRequest) {
  try {
    const body = await req.json();

    // Validate webhook signature (if configured)
    const signature = req.headers.get('x-twilio-email-event-webhook-signature');
    const timestamp = req.headers.get('x-twilio-email-event-webhook-timestamp');

    if (signature && timestamp) {
      const isValid = verifySendGridSignature(body, signature, timestamp);
      if (!isValid) {
        return NextResponse.json({ error: 'Invalid signature' }, { status: 401 });
      }
    }

    // Process webhook events
    const result = await handleSendGridWebhook(body);

    return NextResponse.json({ success: true, processed: result.processed });
  } catch (error) {
    console.error('SendGrid webhook error:', error);
    return NextResponse.json({ error: 'Webhook processing failed' }, { status: 500 });
  }
}

// SendGrid webhook signature verification
function verifySendGridSignature(
  payload: any,
  signature: string,
  timestamp: string
): boolean {
  const webhookSecret = process.env.SENDGRID_WEBHOOK_SECRET;
  if (!webhookSecret) {
    // If no secret configured, allow all webhooks (not recommended for production)
    return true;
  }

  const crypto = require('crypto');
  const expectedSignature = crypto
    .createHmac('sha256', webhookSecret)
    .update(timestamp + JSON.stringify(payload))
    .digest('base64');

  return signature === expectedSignature;
}
