// SendGrid webhook event processing
// src/lib/sendgrid-email.ts
export async function handleSendGridWebhook(events: any[]): Promise<{
  processed: number;
  errors: number;
}> {
  const results = {
    processed: 0,
    errors: 0
  };

  for (const event of events) {
    try {
      await processSendGridEvent(event);
      results.processed++;
    } catch (error) {
      console.error('Error processing SendGrid event:', error);
      results.errors++;
    }
  }

  return results;
}

async function processSendGridEvent(event: any) {
  const {
    event: eventType,
    email,
    timestamp,
    sg_message_id,
    reason,
    url,
    useragent,
    ip,
    category,
    unique_args,
    ...otherData
  } = event;

  // Find corresponding email job or RSVP
  let emailJob = null;
  let rsvp = null;

  // Try to find by SendGrid message ID
  if (sg_message_id) {
    emailJob = await prisma.emailJob.findFirst({
      where: {
        providerMessageId: sg_message_id
      }
    });
  }

  // Try to find by email address
  if (!emailJob) {
    rsvp = await prisma.rsvp.findUnique({
      where: { email }
    });
  }

  // Process based on event type
  switch (eventType) {
    case 'delivered':
      await handleEmailDelivered(emailJob, rsvp, event);
      break;
    case 'bounce':
      await handleEmailBounce(emailJob, rsvp, event);
      break;
    case 'dropped':
      await handleEmailDropped(emailJob, rsvp, event);
      break;
    case 'open':
      await handleEmailOpen(emailJob, rsvp, event);
      break;
    case 'click':
      await handleEmailClick(emailJob, rsvp, event);
      break;
    case 'spam_report':
      await handleSpamReport(emailJob, rsvp, event);
      break;
    case 'unsubscribe':
      await handleUnsubscribe(emailJob, rsvp, event);
      break;
    case 'group_unsubscribe':
      await handleGroupUnsubscribe(emailJob, rsvp, event);
      break;
    case 'processed':
      await handleEmailProcessed(emailJob, rsvp, event);
      break;
    default:
      console.log('Unknown SendGrid event type:', eventType);
  }
}
