```typescript
// Webhook security and validation
function validateWebhookRequest(req: NextRequest, webhookSecret?: string): boolean {
  // Check IP whitelist (if configured)
  const allowedIPs = process.env.WEBHOOK_ALLOWED_IPS?.split(',') || [];
  if (allowedIPs.length > 0) {
    const clientIP = req.headers.get('x-forwarded-for')?.split(',')[0]?.trim();
    if (!allowedIPs.includes(clientIP)) {
      return false;
    }
  }

  // Check webhook secret
  if (webhookSecret) {
    const signature = req.headers.get('x-webhook-signature');
    if (!signature) {
      return false;
    }

    const expectedSignature = crypto
      .createHmac('sha256', webhookSecret)
      .update(JSON.stringify(req.body))
      .digest('hex');

    return signature === expectedSignature;
  }

  return true;
}

// Rate limiting for webhooks
export async function checkWebhookRateLimit(
  webhookType: string,
  clientIP: string
): Promise<boolean> {
  const rateLimitKey = `webhook:${webhookType}:${clientIP}`;
  const rateLimit = await getRateLimit(rateLimitKey);

  if (rateLimit.count >= 100) { // 100 requests per hour
    return false;
  }

  await incrementRateLimit(rateLimitKey, 3600); // 1 hour
  return true;
}
