**2. ICS CALENDAR GENERATION**
```typescript
// ICS calendar file generation
// src/app/api/ics/route.ts
import { NextRequest, NextResponse } from 'next/server';
import { createEvent } from 'ics';

export async function GET(req: NextRequest) {
  try {
    const url = new URL(req.url);
    const title = url.searchParams.get('title') || 'Event';
    const start = url.searchParams.get('start') || new Date().toISOString();
    const end = url.searchParams.get('end') || new Date(Date.now() + 2 * 60 * 60 * 1000).toISOString();
    const location = url.searchParams.get('location') || '';
    const description = url.searchParams.get('desc') || '';

    // Parse dates
    const startDate = new Date(start);
    const endDate = new Date(end);

    // Create ICS event
    const { error, value } = createEvent({
      title,
      description,
      location,
      start: [
        startDate.getFullYear(),
        startDate.getMonth() + 1,
        startDate.getDate(),
        startDate.getHours(),
        startDate.getMinutes()
      ],
      end: [
        endDate.getFullYear(),
        endDate.getMonth() + 1,
        endDate.getDate(),
        endDate.getHours(),
        endDate.getMinutes()
      ],
      startInputType: 'utc',
      endInputType: 'utc',
      duration: { hours: 2, minutes: 30 },
      organizer: {
        name: 'Gabriel Lacroix',
        email: 'gabriel@evergreenwebsolutions.ca'
      },
      url: 'https://rsvp.evergreenwebsolutions.ca',
      status: 'CONFIRMED',
      busyStatus: 'BUSY',
      categories: ['Business', 'AI', 'Information Session'],
      alarms: [
        {
          action: 'display',
          trigger: { hours: 24, minutes: 0, before: true },
          description: 'Reminder: AI in Northern BC Information Session tomorrow'
        },
        {
          action: 'display',
          trigger: { hours: 1, minutes: 0, before: true },
          description: 'Reminder: AI in Northern BC Information Session in 1 hour'
        }
      ]
    });

    if (error) {
      console.error('ICS generation error:', error);
      return NextResponse.json({ error: 'Failed to generate calendar event' }, { status: 500 });
    }

    // Return ICS file
    return new NextResponse(value, {
      headers: {
        'Content-Type': 'text/calendar; charset=utf-8',
        'Content-Disposition': 'attachment; filename="ai-event-2025.ics"',
        'Cache-Control': 'no-cache, no-store, must-revalidate'
      }
    });
  } catch (error) {
    console.error('ICS API error:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}
