export async function getEventAnalytics(): Promise<{
  totalPageViews: number;
  totalCalendarDownloads: number;
  totalRSVPs: number;
  conversionRate: number;
  topReferrers: Array<{ source: string; count: number }>;
  deviceBreakdown: Array<{ device: string; count: number }>;
}> {
  const [pageViews, calendarDownloads, rsvps, referrers, devices] = await Promise.all([
    prisma.eventAnalytics.count({
      where: { eventType: 'page_view' }
    }),
    prisma.eventAnalytics.count({
      where: { eventType: 'calendar_download' }
    }),
    prisma.rsvp.count(),
    prisma.eventAnalytics.groupBy({
      by: ['eventData'],
      where: { eventType: 'page_view' },
      _count: { eventType: true }
    }),
    prisma.eventAnalytics.groupBy({
      by: ['eventData'],
      where: { eventType: 'page_view' },
      _count: { eventType: true }
    })
  ]);

  return {
    totalPageViews: pageViews,
    totalCalendarDownloads: calendarDownloads,
    totalRSVPs: rsvps,
    conversionRate: pageViews > 0 ? (rsvps / pageViews) * 100 : 0,
    topReferrers: [], // Process referrer data
    deviceBreakdown: [] // Process device data
  };
}
