// Fetch businesses from LeadMine
export async function fetchLeadMineBusinesses(params: {
  limit?: number;
  cursor?: string;
  hasEmail?: boolean;
  createMissing?: boolean;
  ids?: string[];
  search?: string;
} = {}): Promise<LeadMineBusinessesResponse> {
  const searchParams = new URLSearchParams();

  if (params.limit) searchParams.set('limit', params.limit.toString());
  if (params.cursor) searchParams.set('cursor', params.cursor);
  if (params.hasEmail !== undefined) searchParams.set('hasEmail', params.hasEmail.toString());
  if (params.createMissing !== undefined) searchParams.set('createMissing', params.createMissing.toString());
  if (params.ids) searchParams.set('ids', params.ids.join(','));
  if (params.search) searchParams.set('search', params.search);

  const queryString = searchParams.toString();
  const path = `/businesses${queryString ? `?${queryString}` : ''}`;

  return leadMineFetch<LeadMineBusinessesResponse>(path);
}

// Fetch specific business by ID
