// Get or create invite token for business
export async function getOrCreateInviteToken(businessId: string): Promise<string> {
  try {
    // First try to get existing token from local database
    const existingMember = await prisma.audienceMember.findFirst({
      where: {
        businessId,
        inviteToken: {
          not: null,
        },
      },
      select: {
        inviteToken: true,
      },
    });

    if (existingMember?.inviteToken) {
      return existingMember.inviteToken;
    }

    // If no local token, fetch from LeadMine
    const business = await fetchLeadMineBusiness(businessId);

    if (business.invite?.token) {
      // Update local database with token
      await prisma.audienceMember.updateMany({
        where: {
          businessId,
        },
        data: {
          inviteToken: business.invite.token,
        },
      });

      return business.invite.token;
    }

    throw new Error('No invite token available for business');
  } catch (error) {
    console.error('Error getting invite token:', error);
    throw error;
  }
}
