**8. ERROR HANDLING AND RETRY LOGIC**
```typescript
// LeadMine error handling with retry logic
export async function leadMineFetchWithRetry<T>(
  path: string,
  init?: RequestInit,
  maxRetries: number = 3
): Promise<T> {
  let lastError: Error;

  for (let attempt = 1; attempt <= maxRetries; attempt++) {
    try {
      return await leadMineFetch<T>(path, init);
    } catch (error) {
      lastError = error;

      // Don't retry on client errors (4xx)
      if (error.message.includes('(4')) {
        throw error;
      }

      // Wait before retry (exponential backoff)
      if (attempt < maxRetries) {
        const delay = Math.pow(2, attempt) * 1000; // 2s, 4s, 8s
        await new Promise(resolve => setTimeout(resolve, delay));
      }
    }
  }

  throw lastError;
}

// Safe LeadMine operations that don't fail the main flow
export async function safeLeadMineOperation<T>(
  operation: () => Promise<T>,
  fallback?: T
): Promise<T | undefined> {
  try {
    return await operation();
  } catch (error) {
    console.error('LeadMine operation failed:', error);
    return fallback;
  }
}
