**3. COMPREHENSIVE VISITOR DATA COLLECTION**
```typescript
// AnalyticsBeacon component collects detailed visitor data
export default function AnalyticsBeacon() {
  useEffect(() => {
    if (typeof window === 'undefined') return;
    if (hasDoNotTrack()) return;

    let sent = false;
    let pointerMoves = 0;
    let clickCount = 0;
    let keypressCount = 0;
    let copyCount = 0;
    let maxScrollPercent = 0;
    let focusEvents = 0;
    let blurEvents = 0;
    let formInteractions = 0;
    let errorCount = 0;
    let resizeCount = 0;
    let beforeUnloadCount = 0;

    const startTime = performance.now();
    const visibilityTimeline: VisibilityEvent[] = [
      { state: document.visibilityState, at: Date.now() },
    ];

    // Scroll depth tracking
    const computeScroll = () => {
      const totalScrollable = document.documentElement.scrollHeight - window.innerHeight;
      if (totalScrollable <= 0) {
        maxScrollPercent = 100;
        return;
      }
      const current = window.scrollY;
      const percent = Math.round((current / totalScrollable) * 100);
      if (percent > maxScrollPercent) {
        maxScrollPercent = Math.min(percent, 100);
      }
    };

    // Event listeners for user interactions
    window.addEventListener('scroll', computeScroll, { passive: true });
    document.addEventListener('click', () => clickCount++);
    document.addEventListener('keydown', () => keypressCount++);
    document.addEventListener('copy', () => copyCount++);
    document.addEventListener('pointermove', () => {
      pointerMoves++;
      if (pointerMoves > 5000) pointerMoves = 5000; // Cap to avoid runaway counts
    }, { passive: true });

    // Visibility change tracking
    const onVisibility = () => {
      const state = document.visibilityState;
      visibilityTimeline.push({ state, at: Date.now() });
      if (state === 'hidden') {
        send('visibilityhidden');
      }
    };
    document.addEventListener('visibilitychange', onVisibility);

    // Performance and connection tracking
    const conn = (navigator as any).connection;
    const onConnectionChange = () => {
      // Track connection changes
    };
    if (conn?.addEventListener) {
      conn.addEventListener('change', onConnectionChange);
    } else if (conn) {
      conn.onchange = onConnectionChange;
    }

    // Build comprehensive payload
    const buildPayload = async () => {
      const doc = document.documentElement;
      const nav = navigator as any;

      return {
        path: window.location.pathname,
        query: window.location.search,
        referrer: document.referrer,

        // Device and browser info
        userAgent: navigator.userAgent,
        language: navigator.language,
        languages: navigator.languages,
        tz: Intl.DateTimeFormat().resolvedOptions().timeZone,

        // Screen and viewport
        screenW: window.screen.width,
        screenH: window.screen.height,
        viewportW: window.innerWidth,
        viewportH: window.innerHeight,
        dpr: window.devicePixelRatio,
        orientation: window.screen.orientation?.type,

        // Device capabilities
        deviceMemory: nav.deviceMemory,
        hardwareConcurrency: navigator.hardwareConcurrency,
        maxTouchPoints: nav.maxTouchPoints,

        // Network info
        connection: conn ? {
          downlink: conn.downlink,
          effectiveType: conn.effectiveType,
          rtt: conn.rtt
        } : undefined,

        // Performance metrics
        paint: {
          firstContentfulPaint: performance.getEntriesByType('paint')
            .find(entry => entry.name === 'first-contentful-paint')?.startTime,
          largestContentfulPaint: performance.getEntriesByType('largest-contentful-paint')
            .pop()?.startTime
        },

        performance: {
          navigationStart: performance.timing.navigationStart,
          loadEventEnd: performance.timing.loadEventEnd,
          domContentLoadedEventEnd: performance.timing.domContentLoadedEventEnd
        },

        // User interactions
        interactionCounts: {
          clicks: clickCount,
          keypresses: keypressCount,
          copies: copyCount,
          pointerMoves,
        },

        // Page metrics
        scrollDepth: maxScrollPercent,
        timeOnPageMs: Math.round(performance.now() - startTime),
        visibility: visibilityTimeline,

        // Storage info
        storage: {
          localStorage: JSON.stringify(localStorage).length,
          sessionStorage: JSON.stringify(sessionStorage).length
        },

        // Navigation info
        navigation: {
          type: nav.connection?.type,
          redirectCount: performance.navigation.redirectCount
        }
      };
    };

    // Send analytics data
    const send = async (reason: string) => {
      if (sent) return;
      sent = true;

      try {
        const payload = await buildPayload();
        const serialized = JSON.stringify(payload, (_key, value) => 
          value === undefined ? undefined : value
        );

        if (navigator.sendBeacon) {
          const blob = new Blob([serialized], { type: 'application/json' });
          navigator.sendBeacon('/api/track/visit', blob);
        } else {
          await fetch('/api/track/visit', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            keepalive: true,
            cache: 'no-store',
            body: serialized,
          });
        }
      } catch (err) {
        // Ignore errors
      }
    };

    // Send on page unload
    window.addEventListener('pagehide', () => send('pagehide'));
    window.addEventListener('beforeunload', () => send('beforeunload'));

    // Fallback timeout
    const fallbackTimeout = window.setTimeout(() => send('timeout'), 10000);

    return () => {
      window.clearTimeout(fallbackTimeout);
      // Cleanup event listeners
    };
  }, []);

  return null;
}
```

