// API endpoint for processing visit data
export async function POST(req: Request) {
  try {
    const body = await req.json();
    const { path, query, referrer, userAgent, language, languages, tz, 
            screenW, screenH, viewportW, viewportH, dpr, orientation,
            deviceMemory, hardwareConcurrency, maxTouchPoints, connection,
            paint, performance, interactionCounts, scrollDepth, 
            timeOnPageMs, visibility, storage, navigation } = body;

    // Get visitor and session IDs from cookies
    const cookies = req.cookies;
    const headers = req.headers;

    const visitorId = cookies.get('vid')?.value ?? 'unknown';
    const sessionId = cookies.get('sid')?.value ?? 'unknown';

    // Get UTM parameters
    const eid = cookies.get('eid')?.value;
    const utmSource = cookies.get('utm_source')?.value;
    const utmMedium = cookies.get('utm_medium')?.value;
    const utmCampaign = cookies.get('utm_campaign')?.value;
    const utmTerm = cookies.get('utm_term')?.value;
    const utmContent = cookies.get('utm_content')?.value;
    const gclid = cookies.get('gclid')?.value;
    const fbclid = cookies.get('fbclid')?.value;
    const msclkid = cookies.get('msclkid')?.value;

    // Get geolocation data
    const country = headers.get('x-vercel-ip-country');
    const region = headers.get('x-vercel-ip-country-region');
    const city = headers.get('x-vercel-ip-city');
    const ip = headers.get('x-forwarded-for')?.split(',')[0]?.trim();
    const ipHash = ip ? crypto.createHash('sha256').update(ip).digest('hex') : undefined;

    // Parse user agent for device info
    const ua = userAgent || undefined;
