```typescript
// Consent management system
// src/lib/consent.ts
export type ConsentType = 
  | 'analytics'
  | 'marketing'
  | 'functional'
  | 'necessary';

export type ConsentStatus = {
  analytics: boolean;
  marketing: boolean;
  functional: boolean;
  necessary: boolean; // Always true, cannot be disabled
  timestamp: string;
  version: string;
};

export function getConsentStatus(): ConsentStatus {
  if (typeof window === 'undefined') {
    return {
      analytics: false,
      marketing: false,
      functional: false,
      necessary: true,
      timestamp: new Date().toISOString(),
      version: '1.0'
    };
  }

  const stored = localStorage.getItem('consent_status');
  if (stored) {
    try {
      return JSON.parse(stored);
    } catch {
      // Invalid stored data, return default
    }
  }

  return {
    analytics: false,
    marketing: false,
    functional: false,
    necessary: true,
    timestamp: new Date().toISOString(),
    version: '1.0'
  };
}

export function setConsentStatus(consent: Partial<ConsentStatus>): void {
  if (typeof window === 'undefined') return;

  const current = getConsentStatus();
  const updated = {
    ...current,
    ...consent,
    timestamp: new Date().toISOString(),
    necessary: true // Always true
  };

  localStorage.setItem('consent_status', JSON.stringify(updated));

  // Trigger consent change event
  window.dispatchEvent(new CustomEvent('consentChanged', { detail: updated }));
}

export function hasConsent(type: ConsentType): boolean {
  const status = getConsentStatus();
  return status[type] || false;
}
