      case 'erasure':
        return await handleErasureRequest(userEmail);
      case 'portability':
        return await handlePortabilityRequest(userEmail);
      case 'restriction':
        return await handleRestrictionRequest(userEmail, additionalData);
      default:
        return { success: false, error: 'Invalid request type' };
    }
  } catch (error) {
    console.error('Data subject request error:', error);
    return { success: false, error: 'Request processing failed' };
  }
}

// Access request - provide all data about the user
async function handleAccessRequest(userEmail: string) {
  const userData = {
    rsvp: await prisma.rsvp.findUnique({
      where: { email: userEmail }
    }),
    visits: await prisma.visit.findMany({
      where: { 
