// src/components/AnalyticsBeacon.tsx
const hasDoNotTrack = () => {
  if (typeof window === 'undefined') return false;

  const dnt = navigator.doNotTrack;
  const gpc = (navigator as any).globalPrivacyControl;
  const signals = [dnt, gpc].filter(Boolean);

  return signals.includes('1') || signals.includes('yes') || gpc === true;
};

