      'https://rsvp.evergreenwebsolutions.ca'
    ];

    // Rate limiting
    const rateLimitCheck = checkRSVPRateLimit(clientIP, userAgent, acceptLanguage, acceptEncoding);
    if (!rateLimitCheck.allowed) {
      logRateLimitViolation(clientIP, '/api/rsvp');
      return createSecureResponse({ message: 'Rate limit exceeded' }, 429);
    }

    // Parse and validate form data
    const body = await req.json();
    const validatedData = rsvpSchema.parse(body);

    // Create RSVP record
    const rsvp = await prisma.rsvp.create({
      data: {
        fullName: `${validatedData.firstName} ${validatedData.lastName}`,
        organization: validatedData.organization,
