// Audience Group API routes
// src/app/api/admin/campaign/groups/route.ts
import { NextRequest, NextResponse } from 'next/server';
import { requireAdminSession } from '@/lib/adminSession';
import prisma from '@/lib/prisma';

export async function GET() {
  const auth = requireAdminSession();
  if ('response' in auth) return auth.response;

  const groups = await prisma.audienceGroup.findMany({
    include: {
      members: true,
      _count: {
        select: {
          members: true,
        },
      },
    },
    orderBy: { createdAt: 'desc' },
  });

  return NextResponse.json({ groups });
}

export async function POST(request: NextRequest) {
  const auth = requireAdminSession();
  if ('response' in auth) return auth.response;

  try {
    const body = await request.json();
    const { name, description, color, criteria, meta } = body;

    const group = await prisma.audienceGroup.create({
      data: {
        name,
        description,
        color: color || '#10b981',
        criteria: criteria || {},
        meta: meta || {},
      },
      include: {
        members: true,
        _count: {
          select: {
            members: true,
          },
        },
      },
    });

    return NextResponse.json({ group });
  } catch (error) {
    console.error('Error creating audience group:', error);
    return NextResponse.json(
      { error: 'Failed to create audience group' },
      { status: 500 }
    );
  }
}
