**4. AUDIENCE MEMBER MANAGEMENT**
```typescript
// Audience Member management functions
// src/lib/campaigns.ts
export async function addAudienceMember(
  groupId: string,
  businessData: {
    businessId: string;
    businessName?: string;
    primaryEmail: string;
    secondaryEmail?: string;
    tagsSnapshot?: string[];
    inviteToken?: string;
    meta?: any;
  }
) {
  try {
    const member = await prisma.audienceMember.create({
      data: {
        groupId,
        businessId: businessData.businessId,
        businessName: businessData.businessName,
        primaryEmail: businessData.primaryEmail,
        secondaryEmail: businessData.secondaryEmail,
        tagsSnapshot: businessData.tagsSnapshot || [],
        inviteToken: businessData.inviteToken,
        meta: businessData.meta || {},
      },
    });

    return member;
  } catch (error) {
    if (error.code === 'P2002') {
      // Unique constraint violation - member already exists
      throw new Error('Member already exists in this group');
    }
    throw error;
  }
}

export async function removeAudienceMember(groupId: string, businessId: string) {
  try {
    await prisma.audienceMember.delete({
      where: {
        groupId_businessId: {
          groupId,
          businessId,
