**6. RATE LIMITING MIDDLEWARE**
```typescript
// Rate limiting middleware
// src/lib/rate-limiting.ts
export class RateLimiter {
  private static instance: RateLimiter;
  private limits: Map<string, { count: number; resetTime: number }> = new Map();

  static getInstance(): RateLimiter {
    if (!RateLimiter.instance) {
      RateLimiter.instance = new RateLimiter();
    }
    return RateLimiter.instance;
  }

  // Check rate limit
  checkRateLimit(
    key: string,
    maxRequests: number,
    windowMs: number
  ): { allowed: boolean; remaining: number; resetTime: number } {
    const now = Date.now();
    const limit = this.limits.get(key);

    if (!limit || now > limit.resetTime) {
      // Create new limit
      this.limits.set(key, {
        count: 1,
        resetTime: now + windowMs
      });

      return {
        allowed: true,
        remaining: maxRequests - 1,
        resetTime: now + windowMs
      };
    }

    if (limit.count >= maxRequests) {
      return {
        allowed: false,
        remaining: 0,
        resetTime: limit.resetTime
      };
    }

    // Increment count
    limit.count++;
    this.limits.set(key, limit);

    return {
      allowed: true,
      remaining: maxRequests - limit.count,
      resetTime: limit.resetTime
    };
  }

  // Clean up expired limits
  cleanupExpiredLimits(): void {
    const now = Date.now();
    for (const [key, limit] of this.limits) {
      if (now > limit.resetTime) {
        this.limits.delete(key);
      }
    }
  }
}

// Rate limiting for RSVP submissions
export function checkRSVPRateLimit(
  clientIP: string,
  userAgent: string,
  acceptLanguage: string,
  acceptEncoding: string
): { allowed: boolean; remaining: number; resetTime: number } {
  const fingerprint = crypto
    .createHash('sha256')
    .update(`${clientIP}-${userAgent}-${acceptLanguage}-${acceptEncoding}`)
    .digest('hex');

  const rateLimiter = RateLimiter.getInstance();
  return rateLimiter.checkRateLimit(
    `rsvp:${fingerprint}`,
    5, // 5 requests
    60 * 60 * 1000 // 1 hour
  );
}
