  const adminProtection = protectAdminRoutes(req);
  if (adminProtection) {
    return adminProtection;
  }

  // Rate limiting for API routes
  if (req.nextUrl.pathname.startsWith('/api/')) {
    const clientIP = req.headers.get('x-forwarded-for')?.split(',')[0]?.trim() || 'unknown';
    const userAgent = req.headers.get('user-agent') || '';
    const acceptLanguage = req.headers.get('accept-language') || '';
    const acceptEncoding = req.headers.get('accept-encoding') || '';

    const rateLimit = checkRSVPRateLimit(clientIP, userAgent, acceptLanguage, acceptEncoding);
    if (!rateLimit.allowed) {
      return NextResponse.json(
        { error: 'Rate limit exceeded' },
        { status: 429 }
      );
    }
  }

  // Visitor and session management
  const url = req.nextUrl;

  // Visitor ID management
  let vid = req.cookies.get('vid')?.value;
  if (!vid) {
    vid = crypto.randomUUID();
    res.cookies.set('vid', vid, {
      path: '/',
      httpOnly: true,
      sameSite: 'lax',
      secure: true,
      maxAge: 60 * 60 * 24 * 365 * 2, // 2 years
    });
  }

  // Session ID management
  const now = Date.now();
  let sid = req.cookies.get('sid')?.value;
  const sidTs = Number(req.cookies.get('sid_ts')?.value || 0);
  if (!sid || now - sidTs > SESSION_MS) {
    sid = crypto.randomUUID();
    res.cookies.set('sid', sid, { 
      path: '/', 
      httpOnly: true, 
      sameSite: 'lax', 
      secure: true, 
      maxAge: 60 * 60 * 24 
    });
    res.cookies.set('sid_ts', String(now), { 
      path: '/', 
      httpOnly: true, 
      sameSite: 'lax', 
      secure: true, 
      maxAge: 60 * 60 * 24 
    });
  } else {
    res.cookies.set('sid_ts', String(now), { 
      path: '/', 
      httpOnly: true, 
      sameSite: 'lax', 
      secure: true, 
      maxAge: 60 * 60 * 24 
    });
  }

  // UTM parameter persistence
  const sp = url.searchParams;
  const utmKeys = [
    'utm_source', 'utm_medium', 'utm_campaign', 
    'utm_term', 'utm_content', 'eid',
    'gclid', 'fbclid', 'msclkid'
  ];

  for (const k of utmKeys) {
    const v = sp.get(k);
    if (v) {
      res.cookies.set(k, v, {
        path: '/',
        httpOnly: true,
        sameSite: 'lax',
        secure: true,
        maxAge: 60 * 60 * 24 * 90, // 90 days
      });
    }
  }

  return res;
}
```

This comprehensive middleware system provides cookie management, UTM tracking, visitor identification, security headers, rate limiting, and admin route protection for your RSVP application.
