// React Hook Form integration with Zod
// src/components/RsvpForm.tsx
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { coreRsvpFormSchema } from '@/lib/validators';

export function RsvpForm() {
  const [currentStep, setCurrentStep] = useState(0);

  const form = useForm<RsvpFormValues>({
    resolver: zodResolver(coreRsvpFormSchema),
    mode: 'onTouched', // Show errors on blur
    defaultValues: {
      firstName: '',
      lastName: '',
      email: '',
      phone: '',
      attendanceStatus: 'YES',
      attendeeCount: 1,
      dietaryPreference: 'NONE',
      accessibilityNeeds: '',
      referralSource: 'WORD_OF_MOUTH',
      wantsResources: false,
      wantsAudit: false,
      learningGoal: '',
    },
  });

  // Step-by-step validation
  const validateStep = async (idx: number) => {
    if (idx === 0) {
      // Contact information validation
      return form.trigger(['firstName', 'lastName', 'email', 'phone'] as (keyof RsvpFormValues)[]);
    }
    if (idx === 1) {
      // Attendance validation
      const keys: (keyof RsvpFormValues)[] = ['attendanceStatus'];
      if (form.getValues('attendanceStatus') === 'YES') keys.push('attendeeCount');
      return form.trigger(keys);
    }
    if (idx === 2) {
      // Dietary & accessibility validation
      const keys: (keyof RsvpFormValues)[] = ['dietaryPreference', 'accessibilityNeeds'];
      if (form.getValues('dietaryPreference') === 'OTHER') keys.push('dietaryOther');
      return form.trigger(keys);
    }
    if (idx === 3) {
      // Referral validation
      const keys: (keyof RsvpFormValues)[] = ['referralSource'];
      if (form.getValues('referralSource') === 'OTHER') keys.push('referralOther');
      return form.trigger(keys);
    }
    if (idx === 4) {
      // Additional options validation
      return form.trigger(['wantsResources', 'wantsAudit', 'learningGoal'] as (keyof RsvpFormValues)[]);
    }
    return true;
  };

  const onNext = async () => {
    const ok = await validateStep(currentStep);
    if (ok) {
      setCurrentStep(prev => Math.min(prev + 1, 4));
    }
  };

  const onSubmit = async (data: RsvpFormValues) => {
    try {
      // Validate complete form
      const validatedData = rsvpSchema.parse(data);

      // Submit to API
      const response = await fetch('/api/rsvp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(validatedData)
      });

      if (response.ok) {
        // Success handling
        setCurrentStep(5); // Success step
      } else {
        // Error handling
        const error = await response.json();
        console.error('RSVP submission error:', error);
      }
    } catch (error) {
      if (error instanceof z.ZodError) {
        // Handle validation errors
        error.errors.forEach(err => {
          form.setError(err.path[0] as keyof RsvpFormValues, {
            message: err.message
          });
        });
      } else {
        console.error('Unexpected error:', error);
      }
    }
  };

  return (
    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
      {/* Form steps and validation */}
      {currentStep === 0 && (
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              First Name *
            </label>
            <input
              {...form.register('firstName')}
              className={`mt-1 block w-full rounded-lg border px-3 py-3 text-base text-gray-900 placeholder:text-gray-400 shadow-sm focus:border-brand-sage focus:ring-2 focus:ring-brand-sage/60 ${
                form.formState.errors.firstName ? 'border-red-500' : 'border-gray-300'
              }`}
              placeholder="Enter your first name"
            />
            {form.formState.errors.firstName && (
              <p className="mt-1 text-sm text-red-600">
                {form.formState.errors.firstName.message}
              </p>
            )}
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Last Name *
            </label>
            <input
              {...form.register('lastName')}
              className={`mt-1 block w-full rounded-lg border px-3 py-3 text-base text-gray-900 placeholder:text-gray-400 shadow-sm focus:border-brand-sage focus:ring-2 focus:ring-brand-sage/60 ${
                form.formState.errors.lastName ? 'border-red-500' : 'border-gray-300'
              }`}
              placeholder="Enter your last name"
            />
            {form.formState.errors.lastName && (
              <p className="mt-1 text-sm text-red-600">
                {form.formState.errors.lastName.message}
              </p>
            )}
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Email Address *
            </label>
            <input
              {...form.register('email')}
              type="email"
              className={`mt-1 block w-full rounded-lg border px-3 py-3 text-base text-gray-900 placeholder:text-gray-400 shadow-sm focus:border-brand-sage focus:ring-2 focus:ring-brand-sage/60 ${
                form.formState.errors.email ? 'border-red-500' : 'border-gray-300'
              }`}
              placeholder="Enter your email address"
            />
            {form.formState.errors.email && (
              <p className="mt-1 text-sm text-red-600">
                {form.formState.errors.email.message}
              </p>
            )}
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Phone Number
            </label>
            <PhoneField
              value={form.watch('phone')}
              onChange={(value) => form.setValue('phone', value)}
              onBlur={() => form.trigger('phone')}
            />
            {form.formState.errors.phone && (
              <p className="mt-1 text-sm text-red-600">
                {form.formState.errors.phone.message}
              </p>
            )}
          </div>
        </div>
      )}

      {/* Other form steps... */}

      <div className="flex justify-between">
        <button
          type="button"
          onClick={() => setCurrentStep(prev => Math.max(prev - 1, 0))}
          className="px-4 py-2 text-gray-600 hover:text-gray-800"
          disabled={currentStep === 0}
        >
          Previous
        </button>

        {currentStep < 4 ? (
          <button
            type="button"
            onClick={onNext}
            className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Next
          </button>
        ) : (
          <button
            type="submit"
            className="px-6 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
            disabled={form.formState.isSubmitting}
          >
            {form.formState.isSubmitting ? 'Submitting...' : 'Submit RSVP'}
          </button>
        )}
      </div>
    </form>
  );
}

// API endpoint validation
// src/app/api/rsvp/route.ts
export async function POST(req: Request) {
  try {
    const body = await req.json();

    // Validate with Zod schema
    const validatedData = rsvpSchema.parse(body);

    // Additional security checks
    if (validatedData.email.length > 254) {
      logInvalidInput(clientIP, '/api/rsvp', { email: 'too long' });
      return createSecureResponse(
        { message: 'Invalid email format' }, 
        400
      );
    }

    // Check for potential XSS in text fields
    const textFields = ['firstName', 'lastName', 'organization', 'learningGoal'];
    for (const field of textFields) {
      if (validatedData[field] && /<script|javascript:|on\w+=/i.test(validatedData[field])) {
        logXSSAttempt(clientIP, '/api/rsvp', validatedData[field]);
        return createSecureResponse(
          { message: 'Invalid input detected' }, 
          400
        );
      }
    }

    // Create RSVP record
    const rsvp = await prisma.rsvp.create({
      data: {
        fullName: `${validatedData.firstName} ${validatedData.lastName}`,
        organization: validatedData.organization,
        email: validatedData.email,
        phone: validatedData.phone,
        attendanceStatus: validatedData.attendanceStatus,
        attendeeCount: validatedData.attendeeCount,
        dietaryPreference: validatedData.dietaryPreference,
        dietaryOther: validatedData.dietaryOther,
        accessibilityNeeds: validatedData.accessibilityNeeds,
        referralSource: validatedData.referralSource,
        referralOther: validatedData.referralOther,
        wantsResources: validatedData.wantsResources,
        wantsAudit: validatedData.wantsAudit,
        learningGoal: validatedData.learningGoal,
        // Analytics data...
      }
    });

    return NextResponse.json({ 
      success: true, 
      rsvpId: rsvp.id,
      message: 'RSVP submitted successfully'
    });
  } catch (error) {
    if (error instanceof z.ZodError) {
      const fieldErrors = error.errors.reduce((acc, err) => {
        acc[err.path[0]] = err.message;
        return acc;
      }, {} as Record<string, string>);

      return NextResponse.json({
        error: 'Validation failed',
        fieldErrors
      }, { status: 400 });
    }

    if (error.code === 'P2002') {
      return NextResponse.json({
        error: 'Email already exists',
        fieldErrors: { email: 'This email has already been used for an RSVP' }
      }, { status: 409 });
    }

    console.error('RSVP processing error:', error);
    return NextResponse.json({
      error: 'Internal server error'
    }, { status: 500 });
  }
}

// Phone field with formatting and validation
// src/components/RsvpForm.tsx
function PhoneField({
  value,
  onChange,
  onBlur,
  id = "phone",
}: {
  value?: string;
  onChange: (v: string) => void;
  onBlur?: () => void;
  id?: string;
}) {
  const [displayValue, setDisplayValue] = useState(value || '');

  const formatPhone = (input: string) => {
    const digits = input.replace(/\D/g, '');
    if (digits.length <= 3) return digits;
    if (digits.length <= 6) return `${digits.slice(0, 3)}-${digits.slice(3)}`;
    return `${digits.slice(0, 3)}-${digits.slice(3, 6)}-${digits.slice(6, 10)}`;
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const formatted = formatPhone(e.target.value);
    setDisplayValue(formatted);
    onChange(formatted);
  };

  return (
    <input
      id={id}
      type="tel"
      value={displayValue}
      onChange={handleChange}
      onBlur={onBlur}
      placeholder="123-456-7890"
      className="mt-1 block w-full rounded-lg border border-gray-300 bg-white px-3 py-3 text-base text-gray-900 placeholder:text-gray-400 shadow-sm focus:border-brand-sage focus:ring-2 focus:ring-brand-sage/60"
    />
  );
}

// Comprehensive error handling for validation
const handleFormError = (error: any) => {
  if (error instanceof z.ZodError) {
    const fieldErrors = error.errors.reduce((acc, err) => {
      acc[err.path[0]] = err.message;
      return acc;
    }, {} as Record<string, string>);

    return NextResponse.json({
      error: 'Validation failed',
      fieldErrors
    }, { status: 400 });
  }

  if (error.code === 'P2002') {
    return NextResponse.json({
      error: 'Email already exists',
      fieldErrors: { email: 'This email has already been used for an RSVP' }
    }, { status: 409 });
  }

  return NextResponse.json({
    error: 'Internal server error'
  }, { status: 500 });
};

// Client-side error display
const ErrorMessage = ({ error }: { error: string }) => (
  <div className="mt-1 text-sm text-red-600">
    {error}
  </div>
);

// Field error display
const FieldError = ({ field, errors }: { field: string; errors: any }) => {
  if (errors[field]) {
    return <ErrorMessage error={errors[field]} />;
  }
  return null;
};

