        referralSource: validatedData.referralSource,
        referralOther: validatedData.referralOther,
        wantsResources: validatedData.wantsResources,
        wantsAudit: validatedData.wantsAudit,
        learningGoal: validatedData.learningGoal,
        // Analytics data...
      }
    });

    return NextResponse.json({ 
      success: true, 
      rsvpId: rsvp.id,
      message: 'RSVP submitted successfully'
    });
  } catch (error) {
    if (error instanceof z.ZodError) {
      const fieldErrors = error.errors.reduce((acc, err) => {
        acc[err.path[0]] = err.message;
        return acc;
      }, {} as Record<string, string>);

      return NextResponse.json({
        error: 'Validation failed',
        fieldErrors
      }, { status: 400 });
    }

    if (error.code === 'P2002') {
      return NextResponse.json({
        error: 'Email already exists',
        fieldErrors: { email: 'This email has already been used for an RSVP' }
      }, { status: 409 });
    }

    console.error('RSVP processing error:', error);
    return NextResponse.json({
      error: 'Internal server error'
    }, { status: 500 });
  }
}
