// Security headers implementation
export function createSecureResponse(
  data: any, 
  status: number = 200,
  headers: Record<string, string> = {}
) {
  const response = NextResponse.json(data, { status });

  // Security headers
  response.headers.set('X-Content-Type-Options', 'nosniff');
  response.headers.set('X-Frame-Options', 'DENY');
  response.headers.set('X-XSS-Protection', '1; mode=block');
  response.headers.set('Referrer-Policy', 'strict-origin-when-cross-origin');

  return response;
}

// Admin session security
export function createSessionToken(username: string, secret: string, expiresAt: number) {
  const payload = { username, exp: expiresAt };
  return jwt.sign(payload, secret, { algorithm: 'HS256' });
}

export function verifySessionToken(token: string | undefined, secret: string) {
  if (!token) return null;

  try {
    const decoded = jwt.verify(token, secret, { algorithms: ['HS256'] }) as any;

    // Check if token is expired
    if (decoded.exp && decoded.exp < Date.now() / 1000) {
      return null;
    }

    return { username: decoded.username, expiresAt: decoded.exp };
  } catch (error) {
    return null;
  }
}

