// Session expiry and renewal
// src/lib/admin-auth.ts
export function isSessionExpired(session: { expiresAt: number }): boolean {
  return session.expiresAt < Date.now() / 1000;
}

export function refreshSession(token: string, secret: string): string | null {
  const session = verifySessionToken(token, secret);
  if (!session) return null;

  // Check if session is close to expiry (within 5 minutes)
  const fiveMinutesFromNow = (Date.now() / 1000) + (5 * 60);
  if (session.expiresAt < fiveMinutesFromNow) {
    const newExpiry = getNewExpiry();
    return createSessionToken(session.username, secret, newExpiry);
  }

  return token; // No refresh needed
}

