    const signature = req.headers.get('x-twilio-email-event-webhook-signature');
    const timestamp = req.headers.get('x-twilio-email-event-webhook-timestamp');

    if (signature && timestamp) {
      const isValid = verifySendGridSignature(body, signature, timestamp);
      if (!isValid) {
        return NextResponse.json({ error: 'Invalid signature' }, { status: 401 });
      }
    }

    // Process webhook events
    const result = await handleSendGridWebhook(body);

    return NextResponse.json({ success: true, processed: result.processed });
  } catch (error) {
    console.error('SendGrid webhook error:', error);
    return NextResponse.json({ error: 'Webhook processing failed' }, { status: 500 });
  }
}

// SendGrid webhook signature verification
function verifySendGridSignature(
  payload: any,
  signature: string,
  timestamp: string
): boolean {
  const webhookSecret = process.env.SENDGRID_WEBHOOK_SECRET;
  if (!webhookSecret) {
    // If no secret configured, allow all webhooks (not recommended for production)
    return true;
  }

  const crypto = require('crypto');
  const expectedSignature = crypto
    .createHmac('sha256', webhookSecret)
    .update(timestamp + JSON.stringify(payload))
    .digest('base64');

  return signature === expectedSignature;
}

// SendGrid webhook event processing
// src/lib/sendgrid-email.ts
export async function handleSendGridWebhook(events: any[]): Promise<{
  processed: number;
  errors: number;
}> {
  const results = {
    processed: 0,
    errors: 0
  };

  for (const event of events) {
    try {
      await processSendGridEvent(event);
      results.processed++;
    } catch (error) {
      console.error('Error processing SendGrid event:', error);
      results.errors++;
    }
  }

  return results;
}

async function processSendGridEvent(event: any) {
  const {
    event: eventType,
    email,
    timestamp,
    sg_message_id,
    reason,
    url,
    useragent,
    ip,
    category,
    unique_args,
    ...otherData
  } = event;

  // Find corresponding email job or RSVP
  let emailJob = null;
  let rsvp = null;

  // Try to find by SendGrid message ID
  if (sg_message_id) {
    emailJob = await prisma.emailJob.findFirst({
      where: {
        providerMessageId: sg_message_id
      }
    });
  }

  // Try to find by email address
  if (!emailJob) {
    rsvp = await prisma.rsvp.findUnique({
      where: { email }
    });
  }

  // Process based on event type
  switch (eventType) {
    case 'delivered':
      await handleEmailDelivered(emailJob, rsvp, event);
      break;
    case 'bounce':
      await handleEmailBounce(emailJob, rsvp, event);
      break;
    case 'dropped':
      await handleEmailDropped(emailJob, rsvp, event);
      break;
    case 'open':
      await handleEmailOpen(emailJob, rsvp, event);
      break;
    case 'click':
      await handleEmailClick(emailJob, rsvp, event);
      break;
    case 'spam_report':
      await handleSpamReport(emailJob, rsvp, event);
      break;
    case 'unsubscribe':
      await handleUnsubscribe(emailJob, rsvp, event);
      break;
    case 'group_unsubscribe':
      await handleGroupUnsubscribe(emailJob, rsvp, event);
      break;
    case 'processed':
      await handleEmailProcessed(emailJob, rsvp, event);
      break;
    default:
      console.log('Unknown SendGrid event type:', eventType);
  }
}

// Individual email event handlers
async function handleEmailDelivered(emailJob: any, rsvp: any, event: any) {
  if (emailJob) {
    // Update email job status
    await prisma.emailJob.update({
      where: { id: emailJob.id },
      data: {
        status: 'delivered',
        meta: {
          ...emailJob.meta,
          deliveredAt: new Date(event.timestamp * 1000).toISOString(),
          deliveryData: event
        }
      }
    });

    // Create email event record
    await prisma.emailEvent.create({
      data: {
        jobId: emailJob.id,
        type: 'delivered',
        meta: event
      }
    });
  }

  // Post to LeadMine if applicable
  if (emailJob?.meta?.inviteToken) {
    await postLeadMineEvent({
      token: emailJob.meta.inviteToken,
      businessId: emailJob.meta.businessId,
      type: 'email_delivered',
      meta: {
        messageId: event.sg_message_id,
        deliveredAt: new Date(event.timestamp * 1000).toISOString()
      }
    });
  }
}

async function handleEmailBounce(emailJob: any, rsvp: any, event: any) {
  if (emailJob) {
    // Update email job status
    await prisma.emailJob.update({
      where: { id: emailJob.id },
      data: {
        status: 'bounced',
        error: event.reason,
        meta: {
          ...emailJob.meta,
          bouncedAt: new Date(event.timestamp * 1000).toISOString(),
          bounceData: event
        }
      }
    });

    // Create email event record
    await prisma.emailEvent.create({
      data: {
        jobId: emailJob.id,
        type: 'bounced',
        meta: event
      }
    });
  }

  // Mark audience member as unsubscribed for hard bounces
  if (event.type === 'bounce' && event.reason?.includes('hard')) {
    await prisma.audienceMember.updateMany({
      where: {
        primaryEmail: event.email
      },
      data: {
        unsubscribed: true,
        meta: {
          unsubscribedAt: new Date().toISOString(),
          unsubscribedReason: 'hard_bounce',
          bounceReason: event.reason
        }
      }
    });
  }
}

