// ImprovMX webhook for email receiving
// src/app/api/webhooks/improvmx/route.ts
export async function POST(req: NextRequest) {
  try {
    const body = await req.json();

    // Process incoming email
    const { from, to, subject, body: emailBody, headers } = body;

    // Determine email type and route accordingly
    if (to.includes('bounces@')) {
      await handleBounceEmail({ from, subject, emailBody, headers });
    } else if (to.includes('unsubscribe@')) {
      await handleUnsubscribeEmail({ from, subject, emailBody, headers });
    } else if (to.includes('support@')) {
      await handleSupportEmail({ from, subject, emailBody, headers });
    } else {
      await handleReplyEmail({ from, subject, emailBody, headers });
    }

    return NextResponse.json({ received: true });
  } catch (error) {
    console.error('ImprovMX webhook error:', error);
    return NextResponse.json({ error: 'Webhook processing failed' }, { status: 500 });
  }
}

// Bounce email handling
async function handleBounceEmail({ from, subject, emailBody, headers }) {
  try {
    // Extract original recipient from bounce email
    const recipientMatch = emailBody.match(/Original-Recipient: (.+)/i);
    const recipient = recipientMatch ? recipientMatch[1] : null;

    if (recipient) {
      // Update database to mark email as bounced
      await prisma.audienceMember.updateMany({
        where: { primaryEmail: recipient },
        data: { 
          unsubscribed: true,
          meta: {
            bounceReason: 'Hard bounce',
            bouncedAt: new Date().toISOString(),
            bounceSubject: subject,
            bounceFrom: from
          }
        }
      });

      // Log bounce event
      await prisma.emailEvent.create({
        data: {
          type: 'bounce',
          meta: {
            recipient,
            bounceType: 'hard',
            bounceReason: subject,
            processedAt: new Date().toISOString()
          }
        }
      });
    }
  } catch (error) {
    console.error('Bounce processing failed:', error);
  }
}

// Unsubscribe email handling
async function handleUnsubscribeEmail({ from, subject, emailBody, headers }) {
  try {
    // Extract email address from unsubscribe request
    const emailMatch = from.match(/<(.+)>/);
    const email = emailMatch ? emailMatch[1] : from;

    // Update database to mark as unsubscribed
    await prisma.audienceMember.updateMany({
      where: { primaryEmail: email },
      data: { 
        unsubscribed: true,
        meta: {
          unsubscribedAt: new Date().toISOString(),
          unsubscribeMethod: 'email',
          unsubscribeSubject: subject
        }
      }
    });

    // Log unsubscribe event
    await prisma.emailEvent.create({
      data: {
        type: 'unsubscribe',
        meta: {
          email,
          unsubscribeMethod: 'email',
          processedAt: new Date().toISOString()
        }
      }
    });

    // Send confirmation email
    await sendUnsubscribeConfirmation(email);
  } catch (error) {
    console.error('Unsubscribe processing failed:', error);
  }
}

// Webhook security and validation
function validateWebhookRequest(req: NextRequest, webhookSecret?: string): boolean {
  // Check IP whitelist (if configured)
  const allowedIPs = process.env.WEBHOOK_ALLOWED_IPS?.split(',') || [];
  if (allowedIPs.length > 0) {
    const clientIP = req.headers.get('x-forwarded-for')?.split(',')[0]?.trim();
    if (!allowedIPs.includes(clientIP)) {
      return false;
    }
  }

  // Check webhook secret
  if (webhookSecret) {
    const signature = req.headers.get('x-webhook-signature');
    if (!signature) {
      return false;
    }

    const expectedSignature = crypto
      .createHmac('sha256', webhookSecret)
      .update(JSON.stringify(req.body))
      .digest('hex');

    return signature === expectedSignature;
  }

  return true;
}

// Rate limiting for webhooks
export async function checkWebhookRateLimit(
  webhookType: string,
  clientIP: string
): Promise<boolean> {
  const rateLimitKey = `webhook:${webhookType}:${clientIP}`;
  const rateLimit = await getRateLimit(rateLimitKey);

  if (rateLimit.count >= 100) { // 100 requests per hour
    return false;
  }

  await incrementRateLimit(rateLimitKey, 3600); // 1 hour
  return true;
}

// Webhook monitoring and logging
export class WebhookMonitor {
  private static instance: WebhookMonitor;
  private webhookStats: Map<string, any> = new Map();

  static getInstance(): WebhookMonitor {
    if (!WebhookMonitor.instance) {
      WebhookMonitor.instance = new WebhookMonitor();
    }
    return WebhookMonitor.instance;
  }

  recordWebhookEvent(webhookType: string, eventType: string, success: boolean) {
    const key = `${webhookType}:${eventType}`;
    const stats = this.webhookStats.get(key) || {
      total: 0,
      success: 0,
      errors: 0,
      lastProcessed: null
    };

    stats.total++;
    if (success) {
      stats.success++;
    } else {
      stats.errors++;
    }
    stats.lastProcessed = new Date().toISOString();

    this.webhookStats.set(key, stats);
  }

  getWebhookStats(): Record<string, any> {
    return Object.fromEntries(this.webhookStats);
  }

  getWebhookHealth(): {
    healthy: boolean;
    issues: string[];
  } {
    const issues: string[] = [];

    for (const [key, stats] of this.webhookStats) {
      const errorRate = stats.errors / stats.total;
      if (errorRate > 0.1) { // More than 10% error rate
        issues.push(`${key} has high error rate: ${(errorRate * 100).toFixed(1)}%`);
      }

      const lastProcessed = new Date(stats.lastProcessed);
      const timeSinceLastProcessed = Date.now() - lastProcessed.getTime();
      if (timeSinceLastProcessed > 24 * 60 * 60 * 1000) { // 24 hours
        issues.push(`${key} hasn't processed events in 24+ hours`);
      }
    }

    return {
      healthy: issues.length === 0,
      issues
    };
  }
}

