    const key = `${webhookType}:${eventType}`;
    const stats = this.webhookStats.get(key) || {
      total: 0,
      success: 0,
      errors: 0,
      lastProcessed: null
    };

    stats.total++;
    if (success) {
      stats.success++;
    } else {
      stats.errors++;
    }
    stats.lastProcessed = new Date().toISOString();

    this.webhookStats.set(key, stats);
  }

  getWebhookStats(): Record<string, any> {
    return Object.fromEntries(this.webhookStats);
  }

  getWebhookHealth(): {
    healthy: boolean;
    issues: string[];
  } {
    const issues: string[] = [];

    for (const [key, stats] of this.webhookStats) {
      const errorRate = stats.errors / stats.total;
      if (errorRate > 0.1) { // More than 10% error rate
        issues.push(`${key} has high error rate: ${(errorRate * 100).toFixed(1)}%`);
      }

      const lastProcessed = new Date(stats.lastProcessed);
      const timeSinceLastProcessed = Date.now() - lastProcessed.getTime();
      if (timeSinceLastProcessed > 24 * 60 * 60 * 1000) { // 24 hours
        issues.push(`${key} hasn't processed events in 24+ hours`);
      }
    }

    return {
      healthy: issues.length === 0,
      issues
    };
  }
}
