// LeadMine API client
async function leadMineFetch<T>(path: string, init?: RequestInit): Promise<T> {
  const baseUrl = process.env.LEADMINE_API_BASE;
  const apiKey = process.env.LEADMINE_API_KEY;

  if (!baseUrl || !apiKey) {
    throw new Error('LeadMine integration not configured');
  }

  const res = await fetch(`${baseUrl}${path}`, {
    ...init,
    headers: {
      Authorization: `Bearer ${apiKey}`,
      'Content-Type': 'application/json',
      ...(init?.headers || {}),
    },
    cache: 'no-store',
  });

  if (!res.ok) {
    const text = await res.text().catch(() => '');
    throw new Error(`LeadMine request failed (${res.status}): ${text}`);
  }

  return res.json() as Promise<T>;
}

// Fetch businesses from LeadMine
export async function fetchLeadMineBusinesses(params: {
  limit?: number;
  cursor?: string;
  hasEmail?: boolean;
  createMissing?: boolean;
  ids?: string[];
  search?: string;
} = {}): Promise<LeadMineBusinessesResponse> {
  const searchParams = new URLSearchParams();

  if (params.limit) searchParams.set('limit', params.limit.toString());
  if (params.cursor) searchParams.set('cursor', params.cursor);
  if (params.hasEmail !== undefined) searchParams.set('hasEmail', params.hasEmail.toString());
  if (params.createMissing !== undefined) searchParams.set('createMissing', params.createMissing.toString());
  if (params.ids) searchParams.set('ids', params.ids.join(','));
  if (params.search) searchParams.set('search', params.search);

  const queryString = searchParams.toString();
  const path = `/businesses${queryString ? `?${queryString}` : ''}`;

  return leadMineFetch<LeadMineBusinessesResponse>(path);
}

// Fetch specific business by ID
export async function fetchLeadMineBusiness(businessId: string): Promise<LeadMineBusiness> {
  return leadMineFetch<LeadMineBusiness>(`/businesses/${businessId}`);
}

// Get or create invite token for business
export async function getOrCreateInviteToken(businessId: string): Promise<string> {
  try {
    // First try to get existing token from local database
    const existingMember = await prisma.audienceMember.findFirst({
      where: {
        businessId,
        inviteToken: {
          not: null,
        },
      },
      select: {
        inviteToken: true,
      },
    });

    if (existingMember?.inviteToken) {
      return existingMember.inviteToken;
    }

    // If no local token, fetch from LeadMine
    const business = await fetchLeadMineBusiness(businessId);

    if (business.invite?.token) {
      // Update local database with token
      await prisma.audienceMember.updateMany({
        where: {
          businessId,
        },
        data: {
          inviteToken: business.invite.token,
        },
      });

      return business.invite.token;
    }

    throw new Error('No invite token available for business');
  } catch (error) {
    console.error('Error getting invite token:', error);
    throw error;
  }
}

