// Sync business data from LeadMine to local database
export async function syncBusinessData(
  groupId: string,
  businessIds?: string[]
): Promise<{
  synced: number;
  errors: number;
  errors: string[];
}> {
  const results = {
    synced: 0,
    errors: 0,
    errors: [] as string[]
  };

  try {
    // Fetch businesses from LeadMine
    const leadMineResponse = await fetchLeadMineBusinesses({
      ids: businessIds,
      hasEmail: true,
      createMissing: true,
    });

    // Sync each business
    for (const business of leadMineResponse.data) {
      try {
        await syncBusinessToGroup(groupId, business);
        results.synced++;
      } catch (error) {
        results.errors++;
        results.errors.push(`Failed to sync business ${business.id}: ${error.message}`);
      }
    }
  } catch (error) {
    results.errors++;
    results.errors.push(`Failed to fetch from LeadMine: ${error.message}`);
  }

  return results;
}

// Sync individual business to group
async function syncBusinessToGroup(groupId: string, business: LeadMineBusiness) {
  const memberData = {
    businessId: business.id,
    businessName: business.name,
    primaryEmail: business.contact.primaryEmail,
    secondaryEmail: business.contact.alternateEmail,
    tagsSnapshot: business.contact.tags,
    inviteToken: business.invite?.token,
    meta: {
      lastSynced: new Date().toISOString(),
      leadMineData: {
        address: business.address,
        website: business.website,
        contactPerson: business.contact.contactPerson,
        leadStatus: business.lead.status,
        leadPriority: business.lead.priority,
        assignedTo: business.lead.assignedTo,
        nextFollowUpDate: business.lead.nextFollowUpDate,
        inviteData: business.invite ? {
          emailsSent: business.invite.emailsSent,
          lastEmailSent: business.invite.lastEmailSent,
          visitsCount: business.invite.visitsCount,
          lastVisitedAt: business.invite.lastVisitedAt,
          rsvpsCount: business.invite.rsvpsCount,
          lastRsvpAt: business.invite.lastRsvpAt,
        } : null,
      },
    },
  };

  // Check if member already exists
  const existingMember = await prisma.audienceMember.findUnique({
    where: {
      groupId_businessId: {
        groupId,
        businessId: business.id,
      },
    },
  });

  if (existingMember) {
    // Update existing member
    await prisma.audienceMember.update({
      where: {
        groupId_businessId: {
          groupId,
          businessId: business.id,
        },
      },
      data: memberData,
    });
  } else {
    // Create new member
    await prisma.audienceMember.create({
      data: {
        groupId,
        ...memberData,
      },
    });
  }
}

