// LeadMine error handling with retry logic
export async function leadMineFetchWithRetry<T>(
  path: string,
  init?: RequestInit,
  maxRetries: number = 3
): Promise<T> {
  let lastError: Error;

  for (let attempt = 1; attempt <= maxRetries; attempt++) {
    try {
      return await leadMineFetch<T>(path, init);
    } catch (error) {
      lastError = error;

      // Don't retry on client errors (4xx)
      if (error.message.includes('(4')) {
        throw error;
      }

      // Wait before retry (exponential backoff)
      if (attempt < maxRetries) {
        const delay = Math.pow(2, attempt) * 1000; // 2s, 4s, 8s
        await new Promise(resolve => setTimeout(resolve, delay));
      }
    }
  }

  throw lastError;
}

// Safe LeadMine operations that don't fail the main flow
export async function safeLeadMineOperation<T>(
  operation: () => Promise<T>,
  fallback?: T
): Promise<T | undefined> {
  try {
    return await operation();
  } catch (error) {
    console.error('LeadMine operation failed:', error);
    return fallback;
  }
}

// LeadMine analytics and reporting
export async function getLeadMineAnalytics(groupId: string): Promise<{
  totalBusinesses: number;
  businessesWithEmail: number;
  businessesWithInviteToken: number;
  totalEmailsSent: number;
  totalVisits: number;
  totalRSVPs: number;
  engagementRate: number;
}> {
  const members = await prisma.audienceMember.findMany({
    where: { groupId },
    select: {
      businessId: true,
      primaryEmail: true,
      inviteToken: true,
      meta: true,
    },
  });

  const analytics = {
    totalBusinesses: members.length,
    businessesWithEmail: members.filter(m => m.primaryEmail).length,
    businessesWithInviteToken: members.filter(m => m.inviteToken).length,
    totalEmailsSent: 0,
    totalVisits: 0,
    totalRSVPs: 0,
    engagementRate: 0,
  };

  // Calculate engagement metrics from meta data
  for (const member of members) {
    const leadMineData = member.meta?.leadMineData;
    if (leadMineData?.inviteData) {
      analytics.totalEmailsSent += leadMineData.inviteData.emailsSent || 0;
      analytics.totalVisits += leadMineData.inviteData.visitsCount || 0;
      analytics.totalRSVPs += leadMineData.inviteData.rsvpsCount || 0;
    }
  }

  // Calculate engagement rate
  if (analytics.totalEmailsSent > 0) {
    analytics.engagementRate = (analytics.totalVisits / analytics.totalEmailsSent) * 100;
  }

  return analytics;
}

// LeadMine webhook handling (if applicable)
export async function handleLeadMineWebhook(req: Request): Promise<NextResponse> {
  try {
    const body = await req.json();
    const { type, data } = body;

    switch (type) {
      case 'business_updated':
        await handleBusinessUpdate(data);
        break;
      case 'business_deleted':
        await handleBusinessDeletion(data);
        break;
      case 'invite_token_generated':
        await handleInviteTokenGenerated(data);
        break;
      default:
        console.log('Unknown LeadMine webhook type:', type);
    }

    return NextResponse.json({ success: true });
  } catch (error) {
    console.error('LeadMine webhook error:', error);
    return NextResponse.json({ error: 'Webhook processing failed' }, { status: 500 });
  }
}

