          leadStatus: businessData.lead.status,
          leadPriority: businessData.lead.priority,
          assignedTo: businessData.lead.assignedTo,
          nextFollowUpDate: businessData.lead.nextFollowUpDate,
        },
      },
    },
  });
}

async function handleBusinessDeletion(businessData: { id: string }) {
  // Remove all audience members with this business ID
  await prisma.audienceMember.deleteMany({
    where: { businessId: businessData.id },
  });
}

async function handleInviteTokenGenerated(data: { businessId: string; token: string }) {
  // Update audience members with new invite token
  await prisma.audienceMember.updateMany({
    where: { businessId: data.businessId },
    data: {
      inviteToken: data.token,
      meta: {
        inviteTokenGeneratedAt: new Date().toISOString(),
      },
    },
  });
}

