// In your application
const IMPROVMX_WEBHOOK_URL = 'https://rsvp.evergreenwebsolutions.ca/api/webhooks/improvmx';

// ImprovMX webhook endpoint
export async function POST(req: NextRequest) {
  try {
    const body = await req.json();

    // Process incoming email
    const { from, to, subject, body: emailBody, headers } = body;

    // Determine email type
    if (to.includes('bounces@')) {
      await handleBounceEmail({ from, subject, emailBody });
    } else if (to.includes('unsubscribe@')) {
      await handleUnsubscribeEmail({ from, subject, emailBody });
    } else if (to.includes('support@')) {
      await handleSupportEmail({ from, subject, emailBody });
    } else {
      await handleReplyEmail({ from, subject, emailBody });
    }

    return NextResponse.json({ received: true });
  } catch (error) {
    console.error('ImprovMX webhook error:', error);
    return NextResponse.json({ error: 'Webhook processing failed' }, { status: 500 });
  }
}

// Handle bounced emails
export async function handleBounceEmail({ from, subject, emailBody }) {
  try {
    // Extract original recipient from bounce email
    const recipientMatch = emailBody.match(/Original-Recipient: (.+)/i);
    const recipient = recipientMatch ? recipientMatch[1] : null;

    if (recipient) {
      // Update database to mark email as bounced
      await prisma.audienceMember.updateMany({
        where: { primaryEmail: recipient },
        data: { 
          unsubscribed: true,
          meta: {
            ...existingMeta,
            bounceReason: 'Hard bounce',
            bouncedAt: new Date().toISOString()
          }
        }
      });

      // Log bounce event
      await prisma.emailEvent.create({
        data: {
          type: 'bounce',
          meta: {
            recipient,
            bounceType: 'hard',
            bounceReason: subject,
            processedAt: new Date().toISOString()
          }
        }
      });
    }
  } catch (error) {
    console.error('Bounce processing failed:', error);
  }
}

// Handle unsubscribe emails
export async function handleUnsubscribeEmail({ from, subject, emailBody }) {
  try {
    // Extract email address from unsubscribe request
    const emailMatch = from.match(/<(.+)>/);
    const email = emailMatch ? emailMatch[1] : from;

    // Update database to mark as unsubscribed
    await prisma.audienceMember.updateMany({
      where: { primaryEmail: email },
      data: { 
        unsubscribed: true,
        meta: {
          ...existingMeta,
          unsubscribedAt: new Date().toISOString(),
          unsubscribeMethod: 'email'
        }
      }
    });

    // Log unsubscribe event
    await prisma.emailEvent.create({
      data: {
        type: 'unsubscribe',
        meta: {
          email,
          unsubscribeMethod: 'email',
          processedAt: new Date().toISOString()
        }
      }
    });

    // Send confirmation email
    await sendUnsubscribeConfirmation(email);
  } catch (error) {
    console.error('Unsubscribe processing failed:', error);
  }
}

// Handle reply emails
export async function handleReplyEmail({ from, subject, emailBody }) {
  try {
    // Extract original campaign info from subject
    const campaignMatch = subject.match(/Re: (.+)/i);
    const originalSubject = campaignMatch ? campaignMatch[1] : subject;

    // Find corresponding campaign send
    const campaignSend = await prisma.campaignSend.findFirst({
      where: {
        email: from,
        meta: {
          path: ['template', 'subject'],
          equals: originalSubject
        }
      },
      include: { schedule: { include: { campaign: true } } }
    });

    if (campaignSend) {
      // Log reply event
      await prisma.emailEvent.create({
        data: {
          type: 'reply',
          meta: {
            campaignId: campaignSend.schedule.campaignId,
            scheduleId: campaignSend.scheduleId,
            businessId: campaignSend.businessId,
            replyContent: emailBody,
            processedAt: new Date().toISOString()
          }
        }
      });

      // Send to LeadMine
      await postLeadMineEvent({
        token: campaignSend.inviteToken,
        businessId: campaignSend.businessId,
        type: 'email_reply',
        meta: {
          campaignId: campaignSend.schedule.campaignId,
          replyContent: emailBody,
          repliedAt: new Date().toISOString()
        }
      });
    }
  } catch (error) {
    console.error('Reply processing failed:', error);
  }
}

// Handle support emails
export async function handleSupportEmail({ from, subject, emailBody }) {
  try {
    // Create support ticket
    const ticket = await prisma.supportTicket.create({
      data: {
        email: from,
        subject: subject,
        content: emailBody,
        status: 'open',
        priority: 'normal',
        meta: {
          source: 'email',
          receivedAt: new Date().toISOString()
        }
      }
    });

    // Send auto-reply
    await sendSupportAutoReply(from, ticket.id);

    // Notify admin
    await notifyAdminOfNewTicket(ticket);
  } catch (error) {
