// AnalyticsBeacon component collects detailed visitor data
export default function AnalyticsBeacon() {
  useEffect(() => {
    if (typeof window === 'undefined') return;
    if (hasDoNotTrack()) return;

    let sent = false;
    let pointerMoves = 0;
    let clickCount = 0;
    let keypressCount = 0;
    let copyCount = 0;
    let maxScrollPercent = 0;
    let focusEvents = 0;
    let blurEvents = 0;
    let formInteractions = 0;
    let errorCount = 0;
    let resizeCount = 0;
    let beforeUnloadCount = 0;

    const startTime = performance.now();
    const visibilityTimeline: VisibilityEvent[] = [
      { state: document.visibilityState, at: Date.now() },
    ];

    // Scroll depth tracking
    const computeScroll = () => {
      const totalScrollable = document.documentElement.scrollHeight - window.innerHeight;
      if (totalScrollable <= 0) {
        maxScrollPercent = 100;
        return;
      }
      const current = window.scrollY;
      const percent = Math.round((current / totalScrollable) * 100);
      if (percent > maxScrollPercent) {
        maxScrollPercent = Math.min(percent, 100);
      }
    };

    // Event listeners for user interactions
    window.addEventListener('scroll', computeScroll, { passive: true });
    document.addEventListener('click', () => clickCount++);
    document.addEventListener('keydown', () => keypressCount++);
    document.addEventListener('copy', () => copyCount++);
    document.addEventListener('pointermove', () => {
      pointerMoves++;
      if (pointerMoves > 5000) pointerMoves = 5000; // Cap to avoid runaway counts
    }, { passive: true });

    // Visibility change tracking
    const onVisibility = () => {
      const state = document.visibilityState;
      visibilityTimeline.push({ state, at: Date.now() });
      if (state === 'hidden') {
        send('visibilityhidden');
      }
    };
    document.addEventListener('visibilitychange', onVisibility);

    // Performance and connection tracking
    const conn = (navigator as any).connection;
    const onConnectionChange = () => {
      // Track connection changes
    };
    if (conn?.addEventListener) {
      conn.addEventListener('change', onConnectionChange);
    } else if (conn) {
      conn.onchange = onConnectionChange;
    }

    // Build comprehensive payload
    const buildPayload = async () => {
      const doc = document.documentElement;
      const nav = navigator as any;

      return {
        path: window.location.pathname,
        query: window.location.search,
        referrer: document.referrer,

        // Device and browser info
        userAgent: navigator.userAgent,
        language: navigator.language,
        languages: navigator.languages,
        tz: Intl.DateTimeFormat().resolvedOptions().timeZone,

        // Screen and viewport
        screenW: window.screen.width,
        screenH: window.screen.height,
        viewportW: window.innerWidth,
        viewportH: window.innerHeight,
        dpr: window.devicePixelRatio,
        orientation: window.screen.orientation?.type,

        // Device capabilities
        deviceMemory: nav.deviceMemory,
        hardwareConcurrency: navigator.hardwareConcurrency,
        maxTouchPoints: nav.maxTouchPoints,

        // Network info
        connection: conn ? {
          downlink: conn.downlink,
          effectiveType: conn.effectiveType,
          rtt: conn.rtt
        } : undefined,

        // Performance metrics
        paint: {
          firstContentfulPaint: performance.getEntriesByType('paint')
            .find(entry => entry.name === 'first-contentful-paint')?.startTime,
          largestContentfulPaint: performance.getEntriesByType('largest-contentful-paint')
            .pop()?.startTime
        },

        performance: {
          navigationStart: performance.timing.navigationStart,
          loadEventEnd: performance.timing.loadEventEnd,
          domContentLoadedEventEnd: performance.timing.domContentLoadedEventEnd
        },

        // User interactions
        interactionCounts: {
          clicks: clickCount,
          keypresses: keypressCount,
          copies: copyCount,
          pointerMoves,
        },

        // Page metrics
        scrollDepth: maxScrollPercent,
        timeOnPageMs: Math.round(performance.now() - startTime),
        visibility: visibilityTimeline,

        // Storage info
        storage: {
          localStorage: JSON.stringify(localStorage).length,
          sessionStorage: JSON.stringify(sessionStorage).length
        },

        // Navigation info
        navigation: {
          type: nav.connection?.type,
          redirectCount: performance.navigation.redirectCount
        }
      };
    };

    // Send analytics data
    const send = async (reason: string) => {
      if (sent) return;
      sent = true;

      try {
        const payload = await buildPayload();
        const serialized = JSON.stringify(payload, (_key, value) => 
          value === undefined ? undefined : value
        );

        if (navigator.sendBeacon) {
          const blob = new Blob([serialized], { type: 'application/json' });
          navigator.sendBeacon('/api/track/visit', blob);
        } else {
          await fetch('/api/track/visit', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            keepalive: true,
            cache: 'no-store',
            body: serialized,
          });
        }
      } catch (err) {
        // Ignore errors
      }
    };

    // Send on page unload
    window.addEventListener('pagehide', () => send('pagehide'));
    window.addEventListener('beforeunload', () => send('beforeunload'));

    // Fallback timeout
    const fallbackTimeout = window.setTimeout(() => send('timeout'), 10000);

    return () => {
      window.clearTimeout(fallbackTimeout);
      // Cleanup event listeners
    };
  }, []);

  return null;
}

// API endpoint for processing visit data
export async function POST(req: Request) {
  try {
    const body = await req.json();
    const { path, query, referrer, userAgent, language, languages, tz, 
            screenW, screenH, viewportW, viewportH, dpr, orientation,
            deviceMemory, hardwareConcurrency, maxTouchPoints, connection,
            paint, performance, interactionCounts, scrollDepth, 
            timeOnPageMs, visibility, storage, navigation } = body;

    // Get visitor and session IDs from cookies
    const cookies = req.cookies;
    const headers = req.headers;

    const visitorId = cookies.get('vid')?.value ?? 'unknown';
    const sessionId = cookies.get('sid')?.value ?? 'unknown';

    // Get UTM parameters
    const eid = cookies.get('eid')?.value;
    const utmSource = cookies.get('utm_source')?.value;
    const utmMedium = cookies.get('utm_medium')?.value;
    const utmCampaign = cookies.get('utm_campaign')?.value;
    const utmTerm = cookies.get('utm_term')?.value;
    const utmContent = cookies.get('utm_content')?.value;
    const gclid = cookies.get('gclid')?.value;
    const fbclid = cookies.get('fbclid')?.value;
    const msclkid = cookies.get('msclkid')?.value;

    // Get geolocation data
    const country = headers.get('x-vercel-ip-country');
    const region = headers.get('x-vercel-ip-country-region');
    const city = headers.get('x-vercel-ip-city');
    const ip = headers.get('x-forwarded-for')?.split(',')[0]?.trim();
    const ipHash = ip ? crypto.createHash('sha256').update(ip).digest('hex') : undefined;

    // Parse user agent for device info
    const ua = userAgent || undefined;
    const parsed = ua ? new UAParser(ua).getResult() : undefined;
    const browser = parsed?.browser?.name;
    const device = parsed?.device?.type || 'desktop';
    const platform = parsed?.os?.name;

    // Create visit record
    await prisma.visit.create({
      data: {
        visitorId,
        sessionId,
        path,
        query,
        referrer,
        eid,
        utmSource,
        utmMedium,
        utmCampaign,
        utmTerm,
        utmContent,
        gclid,
        fbclid,
        msclkid,
        userAgent: ua,
        language,
        languages: languages ? JSON.stringify(languages) : undefined,
        tz,
        screenW,
        screenH,
        viewportW,
        viewportH,
        dpr,
        platform,
        device,
        browser,
        country,
        region,
        city,
        ipHash,
        connection: connection ? JSON.stringify(connection) : undefined,
        deviceMemory,
        hardwareConcurrency,
        maxTouchPoints,
        interactionCounts: interactionCounts ? JSON.stringify(interactionCounts) : undefined,
        languages: languages ? JSON.stringify(languages) : undefined,
        navigation: navigation ? JSON.stringify(navigation) : undefined,
        orientation,
        paint: paint ? JSON.stringify(paint) : undefined,
        performance: performance ? JSON.stringify(performance) : undefined,
        scrollDepth,
        storage: storage ? JSON.stringify(storage) : undefined,
        timeOnPageMs,
        visibility: visibility ? JSON.stringify(visibility) : undefined
      }
    });

    return NextResponse.json({ success: true });
  } catch (error) {
    console.error('Visit tracking error:', error);
    return NextResponse.json({ error: 'Failed to track visit' }, { status: 500 });
  }
}

// Link tracking for campaign attribution
export async function GET(req: Request) {
  const url = new URL(req.url);
  const to = url.searchParams.get('to');
  if (!to) return NextResponse.json({ error: 'Missing "to" param' }, { status: 400 });

  const c = cookies();
  const h = (k: string) => headers().get(k);
  const vid = c.get('vid')?.value ?? 'unknown';
  const sid = c.get('sid')?.value ?? 'unknown';

  // Prefer explicit eid in the link; fallback to cookie
  const eid = url.searchParams.get('eid') ?? c.get('eid')?.value ?? undefined;

  // Get UTM parameters
  const utmSource = c.get('utm_source')?.value;
  const utmMedium = c.get('utm_medium')?.value;
  const utmCampaign = c.get('utm_campaign')?.value;
  const utmTerm = c.get('utm_term')?.value;
  const utmContent = c.get('utm_content')?.value;

  // Parse user agent
  const ua = h('user-agent') || undefined;
  const parsed = ua ? new UAParser(ua).getResult() : undefined;
  const browser = parsed?.browser?.name;
  const device = parsed?.device?.type || 'desktop';
  const platform = parsed?.os?.name;

  // Get geolocation
  const referer = h('referer') || undefined;
  const country = h('x-vercel-ip-country') || undefined;
  const region = h('x-vercel-ip-country-region') || undefined;
  const city = h('x-vercel-ip-city') || undefined;
  const ip = h('x-forwarded-for')?.split(',')[0]?.trim();
  const ipHash = ip ? crypto.createHash('sha256').update(ip).digest('hex') : undefined;

  // Track the link click
  try {
    await prisma.visit.create({
      data: {
        visitorId: vid,
        sessionId: sid,
        path: '/api/l',
        query: url.search,
        referrer: referer,
        eid, utmSource, utmMedium, utmCampaign, utmTerm, utmContent,
        userAgent: ua, browser, device, platform,
        country, region, city, ipHash,
      },
    });
  } catch {
    // Stay silent on errors
  }

  return NextResponse.redirect(to, { status: 302 });
}

