// Admin analytics page with comprehensive metrics
export default async function AdminAnalyticsPage({ searchParams }) {
  const [visits, rsvps, campaigns, audienceGroups] = await Promise.all([
    prisma.visit.findMany({
      include: {
        _count: { select: { id: true } }
      },
      orderBy: { createdAt: 'desc' },
      take: 1000
    }),
    prisma.rsvp.findMany({
      orderBy: { createdAt: 'desc' }
    }),
    prisma.campaign.findMany({
      include: {
        schedules: {
          include: {
            template: true,
            group: true,
          },
        },
        _count: {
          select: {
            schedules: true,
          },
        },
      },
      orderBy: { createdAt: 'desc' },
    }),
    prisma.audienceGroup.findMany({
      include: {
        members: true,
        _count: {
          select: {
            members: true,
          },
        },
      },
      orderBy: { createdAt: 'desc' },
    }),
  ]);

  // Calculate overview statistics
  const totalVisits = visits.length;
  const totalRSVPs = rsvps.length;
  const conversionRate = totalVisits > 0 ? (totalRSVPs / totalVisits) * 100 : 0;

  // Calculate average session duration
  const avgSessionDuration = visits.reduce((sum, visit) => {
    if (visit.timeOnPageMs) {
      return sum + visit.timeOnPageMs;
    }
    return sum;
  }, 0) / Math.max(visits.length, 1);

  // Calculate bounce rate
  const bounceRate = visits.reduce((sum, visit) => {
    if (visit.timeOnPageMs && visit.timeOnPageMs < 5000) {
      return sum + 1;
    }
    return sum;
  }, 0) / Math.max(visits.length, 1) * 100;

  // Top countries
  const countryCounts = visits.reduce((acc, visit) => {
    if (visit.country) {
      acc[visit.country] = (acc[visit.country] || 0) + 1;
    }
    return acc;
  }, {} as Record<string, number>);

  return (
    <div className="space-y-6">
      {/* Analytics dashboard with comprehensive metrics */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-semibold">Total Visits</h3>
          <p className="text-3xl font-bold text-blue-600">{totalVisits.toLocaleString()}</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-semibold">Total RSVPs</h3>
          <p className="text-3xl font-bold text-green-600">{totalRSVPs.toLocaleString()}</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-semibold">Conversion Rate</h3>
          <p className="text-3xl font-bold text-purple-600">{conversionRate.toFixed(1)}%</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-semibold">Avg Session Duration</h3>
          <p className="text-3xl font-bold text-orange-600">
            {Math.round(avgSessionDuration / 1000)}s
          </p>
        </div>
      </div>
    </div>
  );
}

