    resolvedFrom: "fallback"
  };
}

// Consent management system
// src/lib/consent.ts
export type ConsentType = 
  | 'analytics'
  | 'marketing'
  | 'functional'
  | 'necessary';

export type ConsentStatus = {
  analytics: boolean;
  marketing: boolean;
  functional: boolean;
  necessary: boolean; // Always true, cannot be disabled
  timestamp: string;
  version: string;
};

export function getConsentStatus(): ConsentStatus {
  if (typeof window === 'undefined') {
    return {
      analytics: false,
      marketing: false,
      functional: false,
      necessary: true,
      timestamp: new Date().toISOString(),
      version: '1.0'
    };
  }

  const stored = localStorage.getItem('consent_status');
  if (stored) {
    try {
      return JSON.parse(stored);
    } catch {
      // Invalid stored data, return default
    }
  }

  return {
    analytics: false,
    marketing: false,
    functional: false,
    necessary: true,
    timestamp: new Date().toISOString(),
    version: '1.0'
  };
}

export function setConsentStatus(consent: Partial<ConsentStatus>): void {
  if (typeof window === 'undefined') return;

  const current = getConsentStatus();
  const updated = {
    ...current,
    ...consent,
    timestamp: new Date().toISOString(),
    necessary: true // Always true
  };

  localStorage.setItem('consent_status', JSON.stringify(updated));

  // Trigger consent change event
  window.dispatchEvent(new CustomEvent('consentChanged', { detail: updated }));
}

export function hasConsent(type: ConsentType): boolean {
  const status = getConsentStatus();
  return status[type] || false;
}

// Data subject rights implementation
// src/lib/data-rights.ts
export async function handleDataSubjectRequest(
  requestType: 'access' | 'rectification' | 'erasure' | 'portability' | 'restriction',
  userEmail: string,
  additionalData?: any
): Promise<{
  success: boolean;
  data?: any;
  error?: string;
}> {
  try {
    switch (requestType) {
      case 'access':
        return await handleAccessRequest(userEmail);
      case 'rectification':
        return await handleRectificationRequest(userEmail, additionalData);
      case 'erasure':
        return await handleErasureRequest(userEmail);
      case 'portability':
        return await handlePortabilityRequest(userEmail);
      case 'restriction':
        return await handleRestrictionRequest(userEmail, additionalData);
      default:
        return { success: false, error: 'Invalid request type' };
    }
  } catch (error) {
    console.error('Data subject request error:', error);
    return { success: false, error: 'Request processing failed' };
  }
}

// Access request - provide all data about the user
async function handleAccessRequest(userEmail: string) {
  const userData = {
    rsvp: await prisma.rsvp.findUnique({
      where: { email: userEmail }
    }),
    visits: await prisma.visit.findMany({
      where: { 
        // Find visits by email through RSVP
        OR: [
          { visitorId: { in: await getVisitorIdsForEmail(userEmail) } }
        ]
      }
    }),
    audienceMembers: await prisma.audienceMember.findMany({
      where: { primaryEmail: userEmail }
    }),
    emailEvents: await prisma.emailEvent.findMany({
      where: {
        job: {
          recipientEmail: userEmail
        }
      }
    })
  };

  return {
    success: true,
    data: {
      personalData: userData,
      dataCategories: [
        'Contact Information',
        'Event Preferences',
        'Website Analytics',
        'Email Engagement',
        'Marketing Attribution'
      ],
      dataSources: [
        'Direct input (RSVP form)',
        'Automatic collection (website analytics)',
        'Email service providers',
        'LeadMine integration'
      ],
      retentionPeriods: {
        'RSVP Data': '2 years after event',
        'Analytics Data': '1 year',
        'Email Data': '3 years',
        'Marketing Data': '2 years'
      }
    }
  };
}

// Rectification request - correct inaccurate data
async function handleRectificationRequest(userEmail: string, corrections: any) {
  const updates = [];

  // Update RSVP data
  if (corrections.rsvp) {
    const rsvpUpdate = await prisma.rsvp.update({
      where: { email: userEmail },
      data: corrections.rsvp
    });
    updates.push('RSVP data updated');
  }

  // Update audience member data
  if (corrections.audience) {
    const audienceUpdate = await prisma.audienceMember.updateMany({
      where: { primaryEmail: userEmail },
      data: corrections.audience
    });
    updates.push('Audience data updated');
  }

  return {
    success: true,
    data: {
      updates,
      timestamp: new Date().toISOString()
    }
  };
}

// Erasure request - delete all user data
async function handleErasureRequest(userEmail: string) {
  const deletions = [];

  // Delete RSVP data
  const rsvpDelete = await prisma.rsvp.deleteMany({
    where: { email: userEmail }
  });
  if (rsvpDelete.count > 0) {
    deletions.push(`${rsvpDelete.count} RSVP records deleted`);
  }

  // Delete audience member data
  const audienceDelete = await prisma.audienceMember.deleteMany({
    where: { primaryEmail: userEmail }
  });
  if (audienceDelete.count > 0) {
    deletions.push(`${audienceDelete.count} audience member records deleted`);
  }

  // Anonymize visit data (keep for analytics but remove personal identifiers)
  const visitAnonymize = await prisma.visit.updateMany({
    where: {
      visitorId: { in: await getVisitorIdsForEmail(userEmail) }
    },
    data: {
      visitorId: 'anonymized',
      sessionId: 'anonymized',
      ipHash: 'anonymized'
    }
  });
  if (visitAnonymize.count > 0) {
    deletions.push(`${visitAnonymize.count} visit records anonymized`);
  }

  return {
    success: true,
    data: {
      deletions,
      timestamp: new Date().toISOString()
    }
  };
}

// Portability request - export user data in machine-readable format
async function handlePortabilityRequest(userEmail: string) {
  const userData = await handleAccessRequest(userEmail);

  // Format data for portability (JSON format)
  const portableData = {
    exportDate: new Date().toISOString(),
    userEmail,
    dataFormat: 'JSON',
    data: userData.data
  };

  return {
    success: true,
    data: portableData
  };
}

