// Data retention and cleanup
// src/lib/data-retention.ts
export const dataRetentionPolicies = {
  rsvp: {
    retentionPeriod: 2 * 365 * 24 * 60 * 60 * 1000, // 2 years
    cleanupAction: 'delete'
  },
  visits: {
    retentionPeriod: 1 * 365 * 24 * 60 * 60 * 1000, // 1 year
    cleanupAction: 'anonymize'
  },
  emailEvents: {
    retentionPeriod: 3 * 365 * 24 * 60 * 60 * 1000, // 3 years
    cleanupAction: 'delete'
  },
  audienceMembers: {
    retentionPeriod: 2 * 365 * 24 * 60 * 60 * 1000, // 2 years
    cleanupAction: 'anonymize'
  }
};

export async function cleanupExpiredData(): Promise<{
  cleaned: number;
  errors: number;
  errors: string[];
}> {
  const results = {
    cleaned: 0,
    errors: 0,
    errors: [] as string[]
  };

  // Clean up RSVP data
  try {
    const rsvpCutoff = new Date(Date.now() - dataRetentionPolicies.rsvp.retentionPeriod);
    const rsvpDelete = await prisma.rsvp.deleteMany({
      where: {
        createdAt: {
          lt: rsvpCutoff
        }
      }
    });
    results.cleaned += rsvpDelete.count;
  } catch (error) {
    results.errors++;
    results.errors.push(`RSVP cleanup failed: ${error.message}`);
  }

  // Anonymize visit data
  try {
    const visitCutoff = new Date(Date.now() - dataRetentionPolicies.visits.retentionPeriod);
    const visitAnonymize = await prisma.visit.updateMany({
      where: {
        createdAt: {
          lt: visitCutoff
        }
      },
      data: {
        visitorId: 'anonymized',
        sessionId: 'anonymized',
        ipHash: 'anonymized'
      }
    });
    results.cleaned += visitAnonymize.count;
  } catch (error) {
    results.errors++;
    results.errors.push(`Visit cleanup failed: ${error.message}`);
  }

  return results;
}

