export default function PrivacyDashboard({ userEmail }: { userEmail: string }) {
  const [consentStatus, setConsentStatus] = useState<ConsentStatus>(getConsentStatus());
  const [userData, setUserData] = useState<any>(null);
  const [loading, setLoading] = useState(false);

  const handleDataRequest = async (requestType: string) => {
    setLoading(true);
    try {
      const result = await fetch('/api/privacy/request', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          type: requestType,
          email: userEmail
        })
      });

      const data = await result.json();
      if (data.success) {
        setUserData(data.data);
      }
    } catch (error) {
      console.error('Data request failed:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleConsentChange = (type: ConsentType, value: boolean) => {
    setConsentStatus(prev => ({
      ...prev,
      [type]: value
    }));
    setConsentStatus({ [type]: value });
  };

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold">Privacy Dashboard</h2>

      {/* Consent Management */}
      <div className="bg-white p-6 rounded-lg shadow">
