        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <div>
              <label className="font-medium">Analytics</label>
              <p className="text-sm text-gray-600">Help us improve our website</p>
            </div>
            <input
              type="checkbox"
              checked={consentStatus.analytics}
              onChange={(e) => handleConsentChange('analytics', e.target.checked)}
              className="h-4 w-4 text-blue-600"
            />
          </div>

          <div className="flex items-center justify-between">
            <div>
              <label className="font-medium">Marketing</label>
              <p className="text-sm text-gray-600">Receive promotional emails</p>
            </div>
            <input
              type="checkbox"
              checked={consentStatus.marketing}
              onChange={(e) => handleConsentChange('marketing', e.target.checked)}
              className="h-4 w-4 text-blue-600"
            />
          </div>
        </div>
      </div>

      {/* Data Rights */}
      <div className="bg-white p-6 rounded-lg shadow">
        <h3 className="text-lg font-semibold mb-4">Your Data Rights</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <button
            onClick={() => handleDataRequest('access')}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
            disabled={loading}
          >
            {loading ? 'Loading...' : 'Request My Data'}
          </button>

          <button
            onClick={() => handleDataRequest('portability')}
            className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
            disabled={loading}
          >
            {loading ? 'Loading...' : 'Export My Data'}
