async function handleAccessRequest(userEmail: string) {
  const userData = {
    rsvp: await prisma.rsvp.findUnique({
      where: { email: userEmail }
    }),
    visits: await prisma.visit.findMany({
      where: { 
        // Find visits by email through RSVP
        OR: [
          { visitorId: { in: await getVisitorIdsForEmail(userEmail) } }
        ]
      }
    }),
    audienceMembers: await prisma.audienceMember.findMany({
      where: { primaryEmail: userEmail }
    }),
    emailEvents: await prisma.emailEvent.findMany({
      where: {
        job: {
          recipientEmail: userEmail
        }
      }
    })
  };

  return {
    success: true,
    data: {
      personalData: userData,
      dataCategories: [
        'Contact Information',
        'Event Preferences',
        'Website Analytics',
        'Email Engagement',
        'Marketing Attribution'
      ],
      dataSources: [
        'Direct input (RSVP form)',
        'Automatic collection (website analytics)',
        'Email service providers',
        'LeadMine integration'
      ],
      retentionPeriods: {
        'RSVP Data': '2 years after event',
        'Analytics Data': '1 year',
        'Email Data': '3 years',
        'Marketing Data': '2 years'
      }
    }
  };
}

- correct inaccurate data
async function handleRectificationRequest(userEmail: string, corrections: any) {
  const updates = [];

  // Update RSVP data
  if (corrections.rsvp) {
    const rsvpUpdate = await prisma.rsvp.update({
      where: { email: userEmail },
      data: corrections.rsvp
    });
    updates.push('RSVP data updated');
  }

  // Update audience member data
  if (corrections.audience) {
    const audienceUpdate = await prisma.audienceMember.updateMany({
      where: { primaryEmail: userEmail },
      data: corrections.audience
    });
    updates.push('Audience data updated');
  }

  return {
    success: true,
    data: {
      updates,
      timestamp: new Date().toISOString()
    }
  };
}

- delete all user data
async function handleErasureRequest(userEmail: string) {
  const deletions = [];

  // Delete RSVP data
  const rsvpDelete = await prisma.rsvp.deleteMany({
    where: { email: userEmail }
  });
  if (rsvpDelete.count > 0) {
    deletions.push(`${rsvpDelete.count} RSVP records deleted`);
  }

  // Delete audience member data
  const audienceDelete = await prisma.audienceMember.deleteMany({
    where: { primaryEmail: userEmail }
  });
  if (audienceDelete.count > 0) {
    deletions.push(`${audienceDelete.count} audience member records deleted`);
  }

  // Anonymize visit data (keep for analytics but remove personal identifiers)
  const visitAnonymize = await prisma.visit.updateMany({
    where: {
      visitorId: { in: await getVisitorIdsForEmail(userEmail) }
    },
    data: {
      visitorId: 'anonymized',
      sessionId: 'anonymized',
      ipHash: 'anonymized'
    }
  });
  if (visitAnonymize.count > 0) {
    deletions.push(`${visitAnonymize.count} visit records anonymized`);
  }

  return {
    success: true,
    data: {
      deletions,
      timestamp: new Date().toISOString()
    }
  };
}

- export user data in machine-readable format
async function handlePortabilityRequest(userEmail: string) {
  const userData = await handleAccessRequest(userEmail);

  // Format data for portability (JSON format)
  const portableData = {
    exportDate: new Date().toISOString(),
    userEmail,
    dataFormat: 'JSON',
    data: userData.data
  };

  return {
    success: true,
    data: portableData
  };
}
