// Complete form submission with analytics
export async function POST(req: Request) {
  try {
    // Security checks
    const clientIP = headers().get('x-forwarded-for')?.split(',')[0]?.trim() || 'unknown';

    // CSRF Protection
    const origin = headers().get('origin');
    const referer = headers().get('referer');
    const allowedOrigins = [
      'http://localhost:3000',
      'http://localhost:3001',
      'https://rsvp.evergreenwebsolutions.ca'
    ];

    // Rate limiting
    const rateLimitCheck = checkRSVPRateLimit(clientIP, userAgent, acceptLanguage, acceptEncoding);
    if (!rateLimitCheck.allowed) {
      logRateLimitViolation(clientIP, '/api/rsvp');
      return createSecureResponse({ message: 'Rate limit exceeded' }, 429);
    }

    // Parse and validate form data
    const body = await req.json();
    const validatedData = rsvpSchema.parse(body);

    // Create RSVP record
    const rsvp = await prisma.rsvp.create({
      data: {
        fullName: `${validatedData.firstName} ${validatedData.lastName}`,
        organization: validatedData.organization,
        email: validatedData.email,
        phone: validatedData.phone,
        attendanceStatus: validatedData.attendanceStatus,
        attendeeCount: validatedData.attendeeCount,
        dietaryPreference: validatedData.dietaryPreference,
        dietaryOther: validatedData.dietaryOther,
        accessibilityNeeds: validatedData.accessibilityNeeds,
        referralSource: validatedData.referralSource,
        referralOther: validatedData.referralOther,
        wantsResources: validatedData.wantsResources,
        wantsAudit: validatedData.wantsAudit,
        learningGoal: validatedData.learningGoal,
        // Analytics data
        visitorId: cookies().get('vid')?.value,
        sessionId: cookies().get('sid')?.value,
        referrer: headers().get('referer'),
        eid: cookies().get('eid')?.value,
        utmSource: cookies().get('utm_source')?.value,
        utmMedium: cookies().get('utm_medium')?.value,
        utmCampaign: cookies().get('utm_campaign')?.value,
        utmTerm: cookies().get('utm_term')?.value,
        utmContent: cookies().get('utm_content')?.value,
        userAgent: headers().get('user-agent'),
        language: headers().get('accept-language'),
        tz: Intl.DateTimeFormat().resolvedOptions().timeZone,
        country: headers().get('x-vercel-ip-country'),
        region: headers().get('x-vercel-ip-country-region'),
        city: headers().get('x-vercel-ip-city'),
        ipHash: crypto.createHash('sha256').update(clientIP).digest('hex')
      }
    });

    // Send confirmation email
    const emailResult = await sendRSVPConfirmation({
      to: validatedData.email,
      name: `${validatedData.firstName} ${validatedData.lastName}`,
      rsvpId: rsvp.id
    });

    // Post to LeadMine
    await postLeadMineEvent({
      token: 'rsvp-submission',
      businessId: rsvp.id,
      type: 'rsvp_submitted',
      meta: {
        rsvpId: rsvp.id,
        email: validatedData.email,
        attendanceStatus: validatedData.attendanceStatus,
        attendeeCount: validatedData.attendeeCount,
        submittedAt: new Date().toISOString()
      }
    });

    return NextResponse.json({ 
      success: true, 
      rsvpId: rsvp.id,
      message: 'RSVP submitted successfully'
    });
  } catch (error) {
    console.error('RSVP submission error:', error);
    return NextResponse.json({ 
