    ipHash: crypto.createHash('sha256').update(
      headers.get('x-forwarded-for')?.split(',')[0]?.trim() || 'unknown'
    ).digest('hex'),

    // Form-specific data
    meta: JSON.stringify({
      formStep: formData.currentStep,
      validationErrors: formData.errors,
      submissionTime: Date.now()
    })
  };
};

// Privacy-preserving data collection
const hasDoNotTrack = () => {
  const dnt = navigator.doNotTrack;
  const gpc = (navigator as any).globalPrivacyControl;
  const signals = [dnt, gpc].filter(Boolean);
  return signals.includes('1') || signals.includes('yes') || gpc === true;
};

// Only collect analytics if user hasn't opted out
if (!hasDoNotTrack()) {
  // Collect analytics data
  const analyticsData = collectRSVPAnalytics(req, formData);
  await prisma.rsvp.create({ data: { ...formData, ...analyticsData } });
}

