// Sync audience members from LeadMine
// src/lib/campaigns.ts
export async function syncAudienceFromLeadMine(
  groupId: string,
  leadMineData: LeadMineBusiness[]
) {
  const results = {
    added: 0,
    updated: 0,
    errors: 0,
    errors: [] as string[]
  };

  for (const business of leadMineData) {
    try {
      // Check if member already exists
      const existingMember = await prisma.audienceMember.findUnique({
        where: {
          groupId_businessId: {
            groupId,
            businessId: business.id,
          },
        },
      });

      if (existingMember) {
        // Update existing member
        await prisma.audienceMember.update({
          where: {
            groupId_businessId: {
              groupId,
              businessId: business.id,
            },
          },
          data: {
            businessName: business.name,
            primaryEmail: business.contact.primaryEmail,
            secondaryEmail: business.contact.alternateEmail,
            tagsSnapshot: business.contact.tags,
            inviteToken: business.invite?.token,
            meta: {
              ...existingMember.meta,
              lastSynced: new Date().toISOString(),
              leadMineData: {
                status: business.lead.status,
                priority: business.lead.priority,
                assignedTo: business.lead.assignedTo,
                nextFollowUpDate: business.lead.nextFollowUpDate,
              },
            },
          },
        });
        results.updated++;
      } else {
        // Create new member
        await prisma.audienceMember.create({
          data: {
            groupId,
            businessId: business.id,
            businessName: business.name,
            primaryEmail: business.contact.primaryEmail,
            secondaryEmail: business.contact.alternateEmail,
            tagsSnapshot: business.contact.tags,
            inviteToken: business.invite?.token,
            meta: {
              lastSynced: new Date().toISOString(),
              leadMineData: {
                status: business.lead.status,
                priority: business.lead.priority,
                assignedTo: business.lead.assignedTo,
                nextFollowUpDate: business.lead.nextFollowUpDate,
              },
            },
          },
        });
        results.added++;
      }
    } catch (error) {
      results.errors++;
      results.errors.push(`Failed to sync business ${business.id}: ${error.message}`);
    }
  }

  return results;
}

// Audience segmentation based on criteria
const segmentAudience = async (groupId: string, criteria: {
  tags?: string[];
  location?: string;
  industry?: string;
  size?: string;
  unsubscribed?: boolean;
}) => {
  const whereClause: any = {
    groupId,
  };

  if (criteria.tags && criteria.tags.length > 0) {
    whereClause.tagsSnapshot = {
      hasSome: criteria.tags,
    };
  }

  if (criteria.unsubscribed !== undefined) {
    whereClause.unsubscribed = criteria.unsubscribed;
  }

  // Add more criteria based on meta fields
  if (criteria.location) {
    whereClause.meta = {
      path: ['location'],
      equals: criteria.location,
    };
  }

  const members = await prisma.audienceMember.findMany({
    where: whereClause,
    include: {
      group: true,
    },
  });

  return members;
};

// Example segmentation
const techMembers = await segmentAudience('tech-group-id', {
