// Audience analytics and reporting
const getAudienceAnalytics = async (groupId: string) => {
  const group = await prisma.audienceGroup.findUnique({
    where: { id: groupId },
    include: {
      members: {
        include: {
          // Include campaign sends for engagement metrics
          group: {
            include: {
              schedules: {
                include: {
                  sends: true,
                },
              },
            },
          },
        },
      },
    },
  });

  if (!group) {
    throw new Error('Group not found');
  }

  const analytics = {
    totalMembers: group.members.length,
    activeMembers: group.members.filter(m => !m.unsubscribed).length,
    unsubscribedMembers: group.members.filter(m => m.unsubscribed).length,

    // Engagement metrics
    totalEmailsSent: group.members.reduce((sum, member) => {
      return sum + (member.group?.schedules?.reduce((scheduleSum, schedule) => {
        return scheduleSum + schedule.sends.length;
      }, 0) || 0);
    }, 0),

    // Tag distribution
    tagDistribution: group.members.reduce((acc, member) => {
      member.tagsSnapshot.forEach(tag => {
        acc[tag] = (acc[tag] || 0) + 1;
      });
      return acc;
    }, {} as Record<string, number>),

    // Recent activity
    recentActivity: group.members
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime())
      .slice(0, 10),
  };

  return analytics;
};

// Bulk operations for audience members
const bulkUpdateAudienceMembers = async (
  groupId: string,
  updates: Array<{
    businessId: string;
    updateData: any;
  }>
) => {
  const results = {
    updated: 0,
    errors: 0,
    errors: [] as string[]
  };

  for (const update of updates) {
    try {
      await prisma.audienceMember.update({
        where: {
          groupId_businessId: {
            groupId,
            businessId: update.businessId,
          },
        },
        data: update.updateData,
      });
      results.updated++;
    } catch (error) {
      results.errors++;
      results.errors.push(`Failed to update ${update.businessId}: ${error.message}`);
    }
  }

  return results;
};

// Bulk unsubscribe
const bulkUnsubscribe = async (groupId: string, businessIds: string[]) => {
  const result = await prisma.audienceMember.updateMany({
    where: {
      groupId,
      businessId: {
        in: businessIds,
      },
    },
    data: {
      unsubscribed: true,
      meta: {
        unsubscribedAt: new Date().toISOString(),
        unsubscribedBy: 'bulk-operation',
      },
    },
  });

  return result;
};

