export class CookieManager {
  private static instance: CookieManager;

  static getInstance(): CookieManager {
    if (!CookieManager.instance) {
      CookieManager.instance = new CookieManager();
    }
    return CookieManager.instance;
  }

  // Set cookie with security defaults
  setCookie(
    name: string,
    value: string,
    options: {
      maxAge?: number;
      httpOnly?: boolean;
      secure?: boolean;
      sameSite?: 'strict' | 'lax' | 'none';
      path?: string;
    } = {}
  ) {
    const defaults = {
      httpOnly: true,
      secure: process.env.NODE_ENV === 'production',
      sameSite: 'lax' as const,
      path: '/',
      maxAge: 60 * 60 * 24 * 30 // 30 days
    };

    const config = { ...defaults, ...options };

    return {
      name,
      value,
      ...config
    };
  }

  // Get cookie value
  getCookie(name: string, cookies: any): string | undefined {
    return cookies.get(name)?.value;
  }

  // Delete cookie
  deleteCookie(name: string) {
    return {
      name,
      value: '',
      maxAge: 0,
      path: '/'
    };
  }

  // Check if cookie exists
  hasCookie(name: string, cookies: any): boolean {
    return cookies.has(name);
  }
}

// UTM parameter management
export class UTMManager {
  private static instance: UTMManager;

  static getInstance(): UTMManager {
    if (!UTMManager.instance) {
      UTMManager.instance = new UTMManager();
    }
    return UTMManager.instance;
  }

  // Extract UTM parameters from URL
  extractUTMParams(url: URL): Record<string, string> {
    const utmParams: Record<string, string> = {};
    const utmKeys = [
      'utm_source', 'utm_medium', 'utm_campaign',
      'utm_term', 'utm_content', 'eid',
      'gclid', 'fbclid', 'msclkid'
    ];

    for (const key of utmKeys) {
      const value = url.searchParams.get(key);
      if (value) {
        utmParams[key] = value;
      }
    }

    return utmParams;
  }

  // Get UTM parameters from cookies
  getUTMFromCookies(cookies: any): Record<string, string> {
    const utmParams: Record<string, string> = {};
    const utmKeys = [
      'utm_source', 'utm_medium', 'utm_campaign',
      'utm_term', 'utm_content', 'eid',
      'gclid', 'fbclid', 'msclkid'
    ];

    for (const key of utmKeys) {
      const value = cookies.get(key)?.value;
      if (value) {
        utmParams[key] = value;
      }
    }

    return utmParams;
  }

  // Validate UTM parameters
  validateUTMParams(params: Record<string, string>): {
    valid: boolean;
    errors: string[];
  } {
    const errors: string[] = [];

    // Check for required UTM parameters
    if (params.utm_source && !params.utm_medium) {
      errors.push('utm_medium is required when utm_source is provided');
    }

    // Validate UTM source
    if (params.utm_source) {
      const validSources = ['google', 'facebook', 'twitter', 'linkedin', 'email', 'direct'];
      if (!validSources.includes(params.utm_source.toLowerCase())) {
        errors.push('Invalid utm_source value');
      }
    }

    // Validate UTM medium
    if (params.utm_medium) {
      const validMediums = ['cpc', 'cpm', 'email', 'social', 'organic', 'referral'];
      if (!validMediums.includes(params.utm_medium.toLowerCase())) {
        errors.push('Invalid utm_medium value');
      }
    }

    return {
      valid: errors.length === 0,
      errors
    };
  }
}

