// src/lib/visitor-tracking.ts
export class VisitorTracker {
  private static instance: VisitorTracker;

  static getInstance(): VisitorTracker {
    if (!VisitorTracker.instance) {
      VisitorTracker.instance = new VisitorTracker();
    }
    return VisitorTracker.instance;
  }

  // Generate visitor ID
  generateVisitorId(): string {
    return crypto.randomUUID();
  }

  // Generate session ID
  generateSessionId(): string {
    return crypto.randomUUID();
  }

  // Check if session is expired
  isSessionExpired(sessionTimestamp: number): boolean {
    const now = Date.now();
    const sessionAge = now - sessionTimestamp;
    return sessionAge > SESSION_MS;
  }

  // Get visitor fingerprint
  getVisitorFingerprint(req: NextRequest): string {
    const userAgent = req.headers.get('user-agent') || '';
    const acceptLanguage = req.headers.get('accept-language') || '';
    const acceptEncoding = req.headers.get('accept-encoding') || '';
    const ip = req.headers.get('x-forwarded-for')?.split(',')[0]?.trim() || 'unknown';

    const fingerprint = `${userAgent}-${acceptLanguage}-${acceptEncoding}-${ip}`;
    return crypto.createHash('sha256').update(fingerprint).digest('hex');
  }

  // Track page visit
  async trackPageVisit(
    req: NextRequest,
    path: string,
    query: string
  ): Promise<void> {
    try {
      const visitorId = req.cookies.get('vid')?.value;
      const sessionId = req.cookies.get('sid')?.value;

      if (!visitorId || !sessionId) {
        return; // Skip tracking if no visitor/session ID
      }

      const utmParams = UTMManager.getInstance().getUTMFromCookies(req.cookies);
      const fingerprint = this.getVisitorFingerprint(req);

      await prisma.visit.create({
        data: {
          visitorId,
          sessionId,
          path,
          query,
          referrer: req.headers.get('referer'),
          ...utmParams,
          userAgent: req.headers.get('user-agent'),
          language: req.headers.get('accept-language'),
          country: req.headers.get('x-vercel-ip-country'),
          region: req.headers.get('x-vercel-ip-country-region'),
          city: req.headers.get('x-vercel-ip-city'),
          ipHash: crypto.createHash('sha256').update(
            req.headers.get('x-forwarded-for')?.split(',')[0]?.trim() || 'unknown'
          ).digest('hex'),
          meta: JSON.stringify({
            fingerprint,
            timestamp: new Date().toISOString()
          })
        }
      });
    } catch (error) {
      console.error('Visitor tracking error:', error);
    }
  }
}

// Link tracking for campaign attribution
// src/app/api/l/route.ts
export async function GET(req: Request) {
  const url = new URL(req.url);
  const to = url.searchParams.get('to');
  if (!to) return NextResponse.json({ error: 'Missing "to" param' }, { status: 400 });

  const c = cookies();
  const h = (k: string) => headers().get(k);
  const vid = c.get('vid')?.value ?? 'unknown';
  const sid = c.get('sid')?.value ?? 'unknown';

  // Prefer explicit eid in the link; fallback to cookie
  const eid = url.searchParams.get('eid') ?? c.get('eid')?.value ?? undefined;

  // Get UTM parameters from cookies
  const utmSource = c.get('utm_source')?.value;
  const utmMedium = c.get('utm_medium')?.value;
  const utmCampaign = c.get('utm_campaign')?.value;
  const utmTerm = c.get('utm_term')?.value;
  const utmContent = c.get('utm_content')?.value;

  // Parse user agent
  const ua = h('user-agent') || undefined;
  const parsed = ua ? new UAParser(ua).getResult() : undefined;
  const browser = parsed?.browser?.name;
  const device = parsed?.device?.type || 'desktop';
  const platform = parsed?.os?.name;

  // Get geolocation
  const referer = h('referer') || undefined;
  const country = h('x-vercel-ip-country') || undefined;
  const region = h('x-vercel-ip-country-region') || undefined;
  const city = h('x-vercel-ip-city') || undefined;
  const ip = h('x-forwarded-for')?.split(',')[0]?.trim();
  const ipHash = ip ? crypto.createHash('sha256').update(ip).digest('hex') : undefined;

  // Track the link click
  try {
    await prisma.visit.create({
      data: {
        visitorId: vid,
        sessionId: sid,
        path: '/api/l',
        query: url.search,
        referrer: referer,
        eid, utmSource, utmMedium, utmCampaign, utmTerm, utmContent,
        userAgent: ua, browser, device, platform,
        country, region, city, ipHash,
      },
    });
  } catch {
    // Stay silent on errors
  }

  return NextResponse.redirect(to, { status: 302 });
}

