// src/lib/security-middleware.ts
export function addSecurityHeaders(res: NextResponse): NextResponse {
  // Security headers
  res.headers.set('X-Content-Type-Options', 'nosniff');
  res.headers.set('X-Frame-Options', 'DENY');
  res.headers.set('X-XSS-Protection', '1; mode=block');
  res.headers.set('Referrer-Policy', 'strict-origin-when-cross-origin');
  res.headers.set('Permissions-Policy', 'camera=(), microphone=(), geolocation=()');

  // Content Security Policy
  res.headers.set(
    'Content-Security-Policy',
    [
      "default-src 'self'",
      "script-src 'self' 'unsafe-inline' 'unsafe-eval' https://vercel.live",
      "style-src 'self' 'unsafe-inline'",
      "img-src 'self' data: https: blob:",
      "font-src 'self' data:",
      "connect-src 'self' https://api.lead-mine.vercel.app https://api.resend.com https://api.sendgrid.com",
      "frame-src 'none'",
      "object-src 'none'",
      "base-uri 'self'",
      "form-action 'self'",
      "frame-ancestors 'none'"
    ].join('; ')
  );

  return res;
}

// Admin route protection middleware
export function protectAdminRoutes(req: NextRequest): NextResponse | null {
  if (req.nextUrl.pathname.startsWith('/admin')) {
    const token = req.cookies.get(getSessionCookieName())?.value;
    const config = getAdminConfig();

    if (!config) {
      return NextResponse.redirect(new URL('/admin/login?error=config', req.url));
    }

    const session = verifySessionToken(token, config.sessionSecret);
    if (!session) {
      const loginUrl = new URL('/admin/login', req.url);
      loginUrl.searchParams.set('next', req.nextUrl.pathname);
      return NextResponse.redirect(loginUrl);
    }
  }

  return null;
}

// Rate limiting middleware
// src/lib/rate-limiting.ts
export class RateLimiter {
  private static instance: RateLimiter;
  private limits: Map<string, { count: number; resetTime: number }> = new Map();

  static getInstance(): RateLimiter {
    if (!RateLimiter.instance) {
      RateLimiter.instance = new RateLimiter();
    }
    return RateLimiter.instance;
  }

  // Check rate limit
  checkRateLimit(
    key: string,
    maxRequests: number,
    windowMs: number
  ): { allowed: boolean; remaining: number; resetTime: number } {
    const now = Date.now();
    const limit = this.limits.get(key);

    if (!limit || now > limit.resetTime) {
      // Create new limit
      this.limits.set(key, {
        count: 1,
        resetTime: now + windowMs
      });

      return {
        allowed: true,
        remaining: maxRequests - 1,
        resetTime: now + windowMs
      };
    }

    if (limit.count >= maxRequests) {
      return {
        allowed: false,
        remaining: 0,
        resetTime: limit.resetTime
      };
    }

    // Increment count
    limit.count++;
    this.limits.set(key, limit);

    return {
      allowed: true,
      remaining: maxRequests - limit.count,
      resetTime: limit.resetTime
    };
  }

  // Clean up expired limits
  cleanupExpiredLimits(): void {
    const now = Date.now();
    for (const [key, limit] of this.limits) {
      if (now > limit.resetTime) {
        this.limits.delete(key);
      }
    }
  }
}

// Rate limiting for RSVP submissions
export function checkRSVPRateLimit(
  clientIP: string,
  userAgent: string,
  acceptLanguage: string,
  acceptEncoding: string
): { allowed: boolean; remaining: number; resetTime: number } {
  const fingerprint = crypto
    .createHash('sha256')
    .update(`${clientIP}-${userAgent}-${acceptLanguage}-${acceptEncoding}`)
    .digest('hex');

  const rateLimiter = RateLimiter.getInstance();
  return rateLimiter.checkRateLimit(
    `rsvp:${fingerprint}`,
    5, // 5 requests
    60 * 60 * 1000 // 1 hour
  );
}

// Complete middleware integration
// middleware.ts
export function middleware(req: NextRequest) {
  const res = NextResponse.next();

  // Add security headers
  addSecurityHeaders(res);

  // Check admin route protection
  const adminProtection = protectAdminRoutes(req);
  if (adminProtection) {
    return adminProtection;
  }

  // Rate limiting for API routes
  if (req.nextUrl.pathname.startsWith('/api/')) {
    const clientIP = req.headers.get('x-forwarded-for')?.split(',')[0]?.trim() || 'unknown';
    const userAgent = req.headers.get('user-agent') || '';
    const acceptLanguage = req.headers.get('accept-language') || '';
    const acceptEncoding = req.headers.get('accept-encoding') || '';

    const rateLimit = checkRSVPRateLimit(clientIP, userAgent, acceptLanguage, acceptEncoding);
    if (!rateLimit.allowed) {
      return NextResponse.json(
        { error: 'Rate limit exceeded' },
        { status: 429 }
      );
    }
  }

  // Visitor and session management
  const url = req.nextUrl;

  // Visitor ID management
  let vid = req.cookies.get('vid')?.value;
  if (!vid) {
    vid = crypto.randomUUID();
    res.cookies.set('vid', vid, {
      path: '/',
      httpOnly: true,
      sameSite: 'lax',
      secure: true,
      maxAge: 60 * 60 * 24 * 365 * 2, // 2 years
    });
  }

  // Session ID management
  const now = Date.now();
  let sid = req.cookies.get('sid')?.value;
  const sidTs = Number(req.cookies.get('sid_ts')?.value || 0);
  if (!sid || now - sidTs > SESSION_MS) {
    sid = crypto.randomUUID();
    res.cookies.set('sid', sid, { 
      path: '/', 
      httpOnly: true, 
      sameSite: 'lax', 
      secure: true, 
      maxAge: 60 * 60 * 24 
    });
    res.cookies.set('sid_ts', String(now), { 
      path: '/', 
      httpOnly: true, 
      sameSite: 'lax', 
      secure: true, 
      maxAge: 60 * 60 * 24 
    });
  } else {
    res.cookies.set('sid_ts', String(now), { 
      path: '/', 
      httpOnly: true, 
      sameSite: 'lax', 
      secure: true, 
      maxAge: 60 * 60 * 24 
    });
  }

  // UTM parameter persistence
  const sp = url.searchParams;
  const utmKeys = [
    'utm_source', 'utm_medium', 'utm_campaign', 
    'utm_term', 'utm_content', 'eid',
    'gclid', 'fbclid', 'msclkid'
  ];

  for (const k of utmKeys) {
    const v = sp.get(k);
    if (v) {
      res.cookies.set(k, v, {
        path: '/',
        httpOnly: true,
        sameSite: 'lax',
        secure: true,
        maxAge: 60 * 60 * 24 * 90, // 90 days
      });
    }
  }

  return res;
}

