// Visitor tracking and session management
// src/lib/visitor-tracking.ts
export class VisitorTracker {
  private static instance: VisitorTracker;

  static getInstance(): VisitorTracker {
    if (!VisitorTracker.instance) {
      VisitorTracker.instance = new VisitorTracker();
    }
    return VisitorTracker.instance;
  }

  // Generate visitor ID
  generateVisitorId(): string {
    return crypto.randomUUID();
  }

  // Generate session ID
  generateSessionId(): string {
    return crypto.randomUUID();
  }

  // Check if session is expired
  isSessionExpired(sessionTimestamp: number): boolean {
    const now = Date.now();
    const sessionAge = now - sessionTimestamp;
    return sessionAge > SESSION_MS;
  }

  // Get visitor fingerprint
  getVisitorFingerprint(req: NextRequest): string {
    const userAgent = req.headers.get('user-agent') || '';
    const acceptLanguage = req.headers.get('accept-language') || '';
    const acceptEncoding = req.headers.get('accept-encoding') || '';
    const ip = req.headers.get('x-forwarded-for')?.split(',')[0]?.trim() || 'unknown';

    const fingerprint = `${userAgent}-${acceptLanguage}-${acceptEncoding}-${ip}`;
    return crypto.createHash('sha256').update(fingerprint).digest('hex');
  }

  // Track page visit
  async trackPageVisit(
    req: NextRequest,
    path: string,
    query: string
  ): Promise<void> {
    try {
      const visitorId = req.cookies.get('vid')?.value;
      const sessionId = req.cookies.get('sid')?.value;

      if (!visitorId || !sessionId) {
        return; // Skip tracking if no visitor/session ID
      }

      const utmParams = UTMManager.getInstance().getUTMFromCookies(req.cookies);
      const fingerprint = this.getVisitorFingerprint(req);

      await prisma.visit.create({
        data: {
          visitorId,
          sessionId,
          path,
          query,
          referrer: req.headers.get('referer'),
          ...utmParams,
          userAgent: req.headers.get('user-agent'),
          language: req.headers.get('accept-language'),
          country: req.headers.get('x-vercel-ip-country'),
          region: req.headers.get('x-vercel-ip-country-region'),
          city: req.headers.get('x-vercel-ip-city'),
          ipHash: crypto.createHash('sha256').update(
            req.headers.get('x-forwarded-for')?.split(',')[0]?.trim() || 'unknown'
          ).digest('hex'),
          meta: JSON.stringify({
            fingerprint,
            timestamp: new Date().toISOString()
          })
        }
      });
    } catch (error) {
      console.error('Visitor tracking error:', error);
    }
  }
}
